/* Ultra-Modern About Section - Black Theme with Tech Animations */

.about-section {
    padding: var(--spacing-5xl) 0;
    background: #000000 !important;
    position: relative;
    overflow: hidden;
    min-height: 100vh;
    display: flex;
    align-items: center;
    width: 100%;
    visibility: visible !important;
    opacity: 1 !important;
    z-index: 1;
}

/* Floating Background Elements */
.about-bg-elements {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 1;
}

.floating-element {
    position: absolute;
    background: radial-gradient(circle, rgba(0, 191, 255, 0.1) 0%, transparent 70%);
    border-radius: 50%;
    animation: float 6s ease-in-out infinite;
}

.element-1 {
    width: 200px;
    height: 200px;
    top: 10%;
    left: 10%;
    animation-delay: 0s;
}

.element-2 {
    width: 150px;
    height: 150px;
    top: 60%;
    right: 15%;
    background: radial-gradient(circle, rgba(57, 255, 20, 0.08) 0%, transparent 70%);
    animation-delay: 2s;
}

.element-3 {
    width: 100px;
    height: 100px;
    bottom: 20%;
    left: 20%;
    background: radial-gradient(circle, rgba(255, 165, 0, 0.06) 0%, transparent 70%);
    animation-delay: 4s;
}

@keyframes float {
    0%, 100% { transform: translateY(0px) scale(1); }
    50% { transform: translateY(-20px) scale(1.05); }
}

/* Container */
.about-container {
    max-width: 1400px;
    margin: 0 auto;
    padding: 0 var(--spacing-lg);
    position: relative;
    z-index: 2;
    width: 100%;
    display: block !important;
    visibility: visible !important;
}

/* Grid Layout */
.about-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: var(--spacing-4xl);
    align-items: center;
    min-height: 80vh;
    width: 100%;
    visibility: visible !important;
    opacity: 1 !important;
}

/* Content Styles */
.about-content {
    opacity: 1;
    transform: translateX(0);
    transition: all 0.8s ease-out;
    visibility: visible !important;
    display: block !important;
}

.about-content.animate {
    opacity: 1;
    transform: translateX(0);
}

/* Ensure content is visible on mobile by default */
@media (max-width: 768px) {
    .about-section {
        min-height: auto;
        padding: var(--spacing-4xl) 0;
    }

    .about-content {
        opacity: 1 !important;
        transform: translateX(0) !important;
        visibility: visible;
    }

    .about-visual {
        opacity: 1 !important;
        transform: translateX(0) !important;
        visibility: visible;
    }
}

.about-title {
    font-size: clamp(2.5rem, 5vw, 4rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--text-primary);
    margin: 0 0 var(--spacing-lg) 0;
    text-shadow: 0 0 30px rgba(0, 191, 255, 0.3);
}

.title-gradient {
    background: linear-gradient(135deg, #00BFFF 0%, #39FF14 50%, #FFA500 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    position: relative;
}

.title-gradient::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: inherit;
    -webkit-background-clip: text;
    background-clip: text;
    filter: blur(20px);
    opacity: 0.5;
    z-index: -1;
}

.about-subtitle {
    font-size: var(--font-size-xl);
    color: var(--electric-blue);
    font-weight: 500;
    margin: 0 0 var(--spacing-2xl) 0;
    text-transform: uppercase;
    letter-spacing: 1px;
    position: relative;
}

.about-subtitle::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 0;
    width: 60px;
    height: 2px;
    background: linear-gradient(90deg, #00BFFF, #39FF14);
    border-radius: 2px;
}

/* Text Content */
.about-text {
    margin-bottom: var(--spacing-3xl);
}

.mission-text {
    font-size: var(--font-size-lg);
    color: var(--text-primary);
    font-weight: 500;
    line-height: 1.7;
    margin-bottom: var(--spacing-lg);
}

.services-text,
.focus-text {
    color: var(--text-secondary);
    font-size: var(--font-size-md);
    line-height: 1.8;
    margin-bottom: var(--spacing-md);
}

/* Industry Badges */
.industry-badges {
    display: flex;
    flex-wrap: wrap;
    gap: var(--spacing-sm);
    margin-top: var(--spacing-lg);
}

.industry-badge {
    display: inline-block;
    background: rgba(0, 191, 255, 0.1);
    border: 1px solid rgba(0, 191, 255, 0.3);
    color: var(--electric-blue);
    padding: var(--spacing-xs) var(--spacing-md);
    border-radius: var(--border-radius-full);
    font-size: var(--font-size-sm);
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.industry-badge::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(0, 191, 255, 0.2), transparent);
    transition: left 0.5s ease;
}

.industry-badge:hover {
    background: rgba(0, 191, 255, 0.2);
    border-color: var(--electric-blue);
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(0, 191, 255, 0.3);
}

.industry-badge:hover::before {
    left: 100%;
}

/* Visual Section */
.about-visual {
    opacity: 1;
    transform: translateX(0);
    transition: all 0.8s ease-out 0.2s;
    display: flex;
    justify-content: center;
    align-items: center;
    visibility: visible !important;
}

.about-visual.animate {
    opacity: 1;
    transform: translateX(0);
}

/* Ensure visual is visible on mobile by default */
@media (max-width: 768px) {
    .about-visual {
        opacity: 1;
        transform: translateX(0);
    }
}

.tech-graphic {
    width: 100%;
    max-width: 500px;
    height: auto;
}

.tech-animation {
    width: 100%;
    height: auto;
    filter: drop-shadow(0 0 30px rgba(0, 191, 255, 0.4));
    transition: filter 0.3s ease;
}

.tech-animation:hover {
    filter: drop-shadow(0 0 50px rgba(0, 191, 255, 0.6));
}





/* Responsive Design - Mobile First */

/* Tablet - 768px and up */
@media (min-width: 768px) {
    .about-section {
        padding: var(--spacing-6xl) 0;
        min-height: 90vh;
    }

    .about-container {
        padding: 0 var(--spacing-2xl);
    }

    .about-grid {
        gap: var(--spacing-5xl);
    }

    .about-title {
        font-size: clamp(3rem, 6vw, 4.5rem);
    }

    .about-subtitle {
        font-size: var(--font-size-2xl);
    }

    .tech-graphic {
        max-width: 600px;
    }

    .element-1 {
        width: 250px;
        height: 250px;
    }

    .element-2 {
        width: 200px;
        height: 200px;
    }

    .element-3 {
        width: 150px;
        height: 150px;
    }
}

/* Desktop - 1024px and up */
@media (min-width: 1024px) {
    .about-container {
        padding: 0 var(--spacing-3xl);
    }

    .about-grid {
        grid-template-columns: 1fr 1fr;
        gap: var(--spacing-6xl);
        align-items: center;
        display: grid !important;
    }

    .about-content {
        opacity: 1 !important;
        transform: translateX(0) !important;
        visibility: visible !important;
        display: block !important;
    }

    .about-visual {
        opacity: 1 !important;
        transform: translateX(0) !important;
        visibility: visible !important;
        display: flex !important;
    }

    .tech-graphic {
        max-width: 700px;
    }
}

/* Large Desktop - 1200px and up */
@media (min-width: 1200px) {
    .about-container {
        padding: 0 var(--spacing-4xl);
    }

    .about-grid {
        grid-template-columns: 1.2fr 0.8fr;
        gap: var(--spacing-8xl);
    }

    .tech-graphic {
        max-width: 800px;
    }
}

/* Extra Large Desktop - 1400px and up */
@media (min-width: 1400px) {
    .about-container {
        padding: 0 var(--spacing-5xl);
    }
}

/* Mobile - All mobile screens */
@media (max-width: 768px) {
    .about-section {
        padding: 60px 0 !important;
        min-height: auto !important;
        display: block !important;
        background: #000000 !important;
        visibility: visible !important;
        opacity: 1 !important;
    }

    .about-container {
        padding: 0 20px !important;
        display: block !important;
        visibility: visible !important;
    }

    .about-grid {
        display: block !important;
        width: 100% !important;
        visibility: visible !important;
        opacity: 1 !important;
    }

    .about-content {
        opacity: 1 !important;
        transform: translateX(0) !important;
        transition: none !important;
        display: block !important;
        visibility: visible !important;
        margin-bottom: 40px;
    }

    .about-visual {
        opacity: 1 !important;
        transform: translateX(0) !important;
        transition: none !important;
        display: block !important;
        visibility: visible !important;
        margin-bottom: 40px;
    }

    .about-title {
        font-size: 1.8rem !important;
        text-align: center !important;
        margin-bottom: 20px !important;
        color: #ffffff !important;
        display: block !important;
        visibility: visible !important;
    }

    .about-subtitle {
        font-size: 1rem !important;
        text-align: center !important;
        margin-bottom: 30px !important;
        color: #00BFFF !important;
        display: block !important;
        visibility: visible !important;
    }

    .about-text {
        text-align: center !important;
        margin-bottom: 30px !important;
        display: block !important;
        visibility: visible !important;
    }

    .mission-text,
    .services-text,
    .focus-text {
        font-size: 0.9rem !important;
        line-height: 1.6 !important;
        color: #cccccc !important;
        margin-bottom: 15px !important;
        display: block !important;
        visibility: visible !important;
    }

    .industry-badges {
        gap: 8px !important;
        justify-content: center !important;
        display: flex !important;
        flex-wrap: wrap !important;
        visibility: visible !important;
    }

    .industry-badge {
        font-size: 0.75rem !important;
        padding: 6px 12px !important;
        display: inline-block !important;
        visibility: visible !important;
        background: rgba(0, 191, 255, 0.1) !important;
        border: 1px solid rgba(0, 191, 255, 0.3) !important;
        color: #00BFFF !important;
        border-radius: 20px !important;
    }

    .tech-graphic {
        max-width: 280px !important;
        margin: 0 auto !important;
        display: block !important;
        visibility: visible !important;
    }

    .element-1,
    .element-2,
    .element-3 {
        display: none !important;
    }
}

/* Extra Mobile Responsive - 320px and down */
@media (max-width: 320px) {
    .about-section {
        padding: var(--spacing-2xl) 0;
    }

    .about-container {
        padding: 0 var(--spacing-sm);
    }

    .about-title {
        font-size: 1.5rem;
    }

    .about-subtitle {
        font-size: var(--font-size-sm);
    }

    .tech-graphic {
        max-width: 240px;
    }
}

