src/components/footer/footer.js.content:
// Footer Component JavaScript
class FooterComponent {
    constructor() {
        this.init();
    }

    async init() {
        await this.loadFooter();
        this.setupFooterInteractions();
    }

    async loadFooter() {
        // Footer HTML is now loaded by the main component loader
        // This method just sets up interactions
        console.log('Footer component initialized');
    }

    setupFooterInteractions() {
        // Smooth scroll for footer links that are anchors
        const footerLinks = document.querySelectorAll('.footer a[href^="#"]');

        footerLinks.forEach(link => {
            link.addEventListener('click', (e) => {
                e.preventDefault();
                const targetId = link.getAttribute('href').substring(1);
                const targetElement = document.getElementById(targetId);

                if (targetElement) {
                    targetElement.scrollIntoView({
                        behavior: 'smooth',
                        block: 'start'
                    });
                }
            });
        });

        // Add hover effects to social links
        const socialLinks = document.querySelectorAll('.social-link');

        socialLinks.forEach(link => {
            link.addEventListener('mouseenter', () => {
                link.style.transform = 'translateY(-2px)';
            });

            link.addEventListener('mouseleave', () => {
                link.style.transform = 'translateY(0)';
            });
        });

        // Newsletter subscription (if present)
        const newsletterForm = document.querySelector('.newsletter-form');
        if (newsletterForm) {
            newsletterForm.addEventListener('submit', (e) => {
                e.preventDefault();
                this.handleNewsletterSubscription(e);
            });
        }
    }

    handleNewsletterSubscription(event) {
        const form = event.target;
        const emailInput = form.querySelector('input[type="email"]');
        const submitButton = form.querySelector('button[type="submit"]');

        if (!emailInput || !emailInput.value.trim()) {
            this.showNotification('Please enter a valid email address.', 'error');
            return;
        }

        // Disable submit button
        submitButton.disabled = true;
        submitButton.innerHTML = '<i class="fas fa-spinner fa-spin"></i> Subscribing...';

        // Simulate newsletter subscription (replace with actual endpoint)
        setTimeout(() => {
            this.showNotification('Thank you for subscribing to our newsletter!', 'success');
            form.reset();

            // Re-enable submit button
            submitButton.disabled = false;
            submitButton.innerHTML = 'Subscribe';
        }, 2000);
    }

    showNotification(message, type = 'info') {
        // Create notification element
        const notification = document.createElement('div');
        notification.className = `notification notification-${type}`;
        notification.style.cssText = `
            position: fixed;
            top: 20px;
            right: 20px;
            background: ${type === 'success' ? 'var(--neon-green)' : type === 'error' ? '#ff4444' : 'var(--electric-blue)'};
            color: var(--text-primary);
            padding: var(--spacing-md) var(--spacing-lg);
            border-radius: var(--border-radius-md);
            box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
            z-index: 10000;
            transform: translateX(400px);
            transition: transform 0.3s ease;
            font-weight: 500;
        `;
        notification.textContent = message;

        document.body.appendChild(notification);

        // Animate in
        setTimeout(() => {
            notification.style.transform = 'translateX(0)';
        }, 100);

        // Remove after 5 seconds
        setTimeout(() => {
            notification.style.transform = 'translateX(400px)';
            setTimeout(() => {
                if (document.body.contains(notification)) {
                    document.body.removeChild(notification);
                }
            }, 300);
        }, 5000);
    }
}

// Initialize footer when DOM is loaded
document.addEventListener('DOMContentLoaded', () => {
    new FooterComponent();
});

src/components/header/header-new.css.content:
/* Navbar Component Styles - Completely Rewritten */

/* Force consistent sizing on all pages */
body .navbar {
    font-size: 1rem !important;
}

body .navbar * {
    font-size: inherit !important;
}

.navbar {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    background: rgba(0, 0, 0, 0.95);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    z-index: 1000;
    transition: all 0.3s ease;
    box-shadow: 0 4px 30px rgba(0, 0, 0, 0.3);
}

.navbar.scrolled {
    background: rgba(0, 0, 0, 0.98);
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.5);
}

.nav-container {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 1rem 2rem;
    max-width: 1400px;
    margin: 0 auto;
    height: 70px;
}

/* Brand */
.nav-brand .brand-link {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    text-decoration: none;
    color: #ffffff;
}

.logo {
    width: 36px;
    height: 36px;
    transition: transform 0.3s ease;
}

.logo:hover {
    transform: scale(1.1);
}

.brand-text {
    font-weight: 600;
    font-size: 1.25rem;
    background: linear-gradient(135deg, #00BFFF, #39FF14);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

/* Desktop Navigation - Isolated Items */
.nav-menu-desktop {
    display: flex;
    align-items: center;
    gap: 0;
}

/* Isolate each nav item to prevent hover bleed */
.nav-menu-desktop > .nav-link,
.nav-menu-desktop > .nav-dropdown {
    position: relative;
    isolation: isolate;
}

.nav-link {
    color: rgba(255, 255, 255, 0.8);
    text-decoration: none;
    font-weight: 500;
    font-size: 1rem !important;
    transition: all 0.3s ease;
    position: relative;
    padding: 0.75rem 1rem;
    display: block;
    border-radius: 8px;
    white-space: nowrap;
    margin: 0 0.5rem;
}

.nav-link:hover {
    color: #00BFFF;
}

.nav-link::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 0;
    height: 2px;
    background: linear-gradient(90deg, #00BFFF, #39FF14);
    transition: width 0.3s ease;
}

.nav-link:hover::after {
    width: 100%;
}

/* Dropdown Styles */
.nav-dropdown {
    position: relative;
}

/* Remove duplicate nav-dropdown */

.dropdown-trigger {
    color: rgba(255, 255, 255, 0.8);
    text-decoration: none;
    font-weight: 500;
    font-size: 1rem !important;
    transition: all 0.3s ease;
    position: relative;
    padding: 0.75rem 1rem;
    display: flex;
    align-items: center;
    gap: 0.5rem;
    border-radius: 8px;
    white-space: nowrap;
    cursor: pointer;
}

.dropdown-trigger:hover {
    color: #00BFFF;
    background: rgba(0, 191, 255, 0.1);
}





.dropdown-trigger i {
    transition: transform 0.3s ease;
    font-size: 0.8em;
}

.nav-dropdown:hover .dropdown-trigger i {
    transform: rotate(180deg);
}

.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    background: rgba(0, 0, 0, 0.95);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 12px;
    padding: 1.5rem;
    min-width: 850px;
    max-width: 900px;
    width: auto;
    box-shadow: 0 25px 50px rgba(0, 0, 0, 0.7);
    opacity: 0;
    visibility: hidden;
    pointer-events: none;
    transform: translateX(-50%) translateY(-10px);
    transition: all 0.2s ease;
    margin-top: 1rem;
    z-index: 1000;
}

/* Remove bridge area to prevent overlap */

/* Precise hover only on dropdown container - no inheritance */
.nav-dropdown:hover > .dropdown-menu {
    opacity: 1;
    visibility: visible;
    pointer-events: all;
    transform: translateX(-50%) translateY(0);
}

/* Keep dropdown open when directly hovering the menu */
.dropdown-menu:hover {
    opacity: 1;
    visibility: visible;
    pointer-events: all;
}

.dropdown-content {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 1.25rem;
    max-width: 900px;
    width: 100%;
}

/* Adjust grid for Industries (2 columns) */
.nav-dropdown:nth-child(3) .dropdown-content {
    grid-template-columns: repeat(2, 1fr);
    max-width: 350px;
    gap: 1rem;
}

/* Adjust dropdown menu width for Industries */
.nav-dropdown:nth-child(3) .dropdown-menu {
    min-width: 350px;
    max-width: 350px;
    padding: 1rem;
}

/* Adjust grid for Products (2 columns) */  
.nav-dropdown:nth-child(4) .dropdown-content {
    grid-template-columns: repeat(2, 1fr);
    max-width: 450px;
    gap: 1.5rem;
}

/* Adjust dropdown menu width for Products */
.nav-dropdown:nth-child(4) .dropdown-menu {
    min-width: 450px;
    max-width: 500px;
    padding: 1rem;
}

.dropdown-section {
    min-height: auto;
}

/* Reduce height for Industries and Products sections */
.nav-dropdown:nth-child(3) .dropdown-section,
.nav-dropdown:nth-child(4) .dropdown-section {
    min-height: auto;
    padding-bottom: 0;
}

.dropdown-section h4 {
    color: #00BFFF;
    font-size: 0.85rem;
    font-weight: 600;
    margin-bottom: 1rem;
    padding-bottom: 0.5rem;
    border-bottom: 2px solid #00BFFF;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    text-align: center;
}

.dropdown-link {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    padding: 0.5rem 0.75rem;
    text-decoration: none;
    color: #ffffff;
    border-radius: 8px;
    transition: all 0.3s ease;
    margin-bottom: 0.25rem;
    border: 1px solid rgba(255, 255, 255, 0.05);
    background: rgba(255, 255, 255, 0.02);
    white-space: nowrap;
}

.dropdown-link:hover {
    background: rgba(0, 191, 255, 0.1);
    color: #00BFFF;
    transform: translateX(4px);
}

.dropdown-link i {
    color: #00BFFF;
    font-size: 0.9rem;
    flex-shrink: 0;
    width: 16px;
    text-align: center;
}

.dropdown-link span {
    font-weight: 500;
    color: #ffffff;
    transition: color 0.3s ease;
    font-size: 0.9rem;
}

.dropdown-link:hover span {
    color: #00BFFF;
}

/* CTA Section in Dropdown */
.dropdown-cta {
    margin-top: 2rem;
    padding: 1.5rem;
    background: linear-gradient(135deg, rgba(0, 191, 255, 0.1), rgba(57, 255, 20, 0.05));
    border-radius: 12px;
    border: 1px solid rgba(0, 191, 255, 0.2);
    text-align: center;
}

.dropdown-cta h5 {
    color: #00BFFF;
    font-size: 1rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
}

.dropdown-cta p {
    color: rgba(255, 255, 255, 0.8);
    font-size: 0.9rem;
    margin-bottom: 1rem;
    line-height: 1.4;
}

.btn-cta-small {
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.75rem 1.25rem;
    background: linear-gradient(135deg, #00BFFF, #39FF14);
    color: #000000;
    text-decoration: none;
    font-weight: 600;
    font-size: 0.9rem;
    border-radius: 8px;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0, 191, 255, 0.3);
}

.btn-cta-small:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(0, 191, 255, 0.4);
    filter: brightness(1.1);
}

/* CTA Button */
.btn-cta {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.75rem 1.5rem;
    background: linear-gradient(135deg, #00BFFF, #39FF14);
    color: #000000;
    text-decoration: none;
    font-weight: 600;
    border-radius: 8px;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0, 191, 255, 0.3);
}

.btn-cta:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(0, 191, 255, 0.4);
    filter: brightness(1.1);
}

/* Mobile Toggle */
.nav-toggle {
    display: none;
    flex-direction: column;
    justify-content: space-between;
    width: 30px;
    height: 22px;
    background: none;
    border: none;
    cursor: pointer;
    padding: 0;
}

.hamburger-line {
    width: 100%;
    height: 3px;
    background: #00BFFF;
    transition: all 0.3s ease;
    border-radius: 2px;
}

.nav-toggle.active .hamburger-line:nth-child(1) {
    transform: rotate(45deg) translate(6px, 6px);
}

.nav-toggle.active .hamburger-line:nth-child(2) {
    opacity: 0;
}

.nav-toggle.active .hamburger-line:nth-child(3) {
    transform: rotate(-45deg) translate(6px, -6px);
}

/* Responsive Design */
@media (max-width: 1023px) {
    /* Hide desktop navigation */
    .nav-menu-desktop {
        display: none;
    }

    /* Hide desktop CTA */
    .desktop-cta {
        display: none;
    }

    /* Show mobile toggle */
    .nav-toggle {
        display: flex;
    }

    .nav-container {
        padding: 1rem 1.5rem;
    }
}

@media (max-width: 768px) {
    .nav-container {
        padding: 1rem;
    }

    .brand-text {
        font-size: 1.1rem;
    }

    .logo {
        width: 32px;
        height: 32px;
    }
}

/* Prevent dropdown overflow on smaller screens */
@media (max-width: 1200px) {
    .dropdown-menu {
        min-width: 700px;
        max-width: 90vw;
    }

    .dropdown-content {
        grid-template-columns: repeat(2, 1fr);
    }
}

/* Animation for dropdown items */
.dropdown-link {
    opacity: 0;
    transform: translateY(10px);
    animation: fadeInUp 0.3s ease forwards;
}

.dropdown-link:nth-child(1) { animation-delay: 0.1s; }
.dropdown-link:nth-child(2) { animation-delay: 0.2s; }
.dropdown-link:nth-child(3) { animation-delay: 0.3s; }
.dropdown-link:nth-child(4) { animation-delay: 0.4s; }
.dropdown-link:nth-child(5) { animation-delay: 0.5s; }
.dropdown-link:nth-child(6) { animation-delay: 0.6s; }

@keyframes fadeInUp {
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

src/components/header/header-new.js.content:
// Navbar Component JavaScript - Completely Rewritten
class NewNavbarComponent {
    constructor() {
        this.navbar = null;
        this.navToggle = null;
        this.mobileNav = null;
        this.dropdowns = [];
        this.isScrolled = false;
        this.lastScrollTop = 0;

        this.init();
    }

    async init() {
        await this.loadNavbar();
        this.loadMobileNavigation(); // Make synchronous
        this.setupEventListeners();
        this.setupScrollHandling();
        this.setupDropdowns();
        this.setupMobileNavigation();
    }

    async loadNavbar() {
        try {
            console.log('Loading new navbar with all 18 services...');
            const response = await fetch('src/components/header/header-new.html?v=' + Date.now());
            const navbarHTML = await response.text();

            // Remove any existing navbar first
            const existingNavbar = document.getElementById('navbar');
            if (existingNavbar) {
                existingNavbar.remove();
            }

            // Insert navbar at the beginning of body
            document.body.insertAdjacentHTML('afterbegin', navbarHTML);
            this.navbar = document.getElementById('navbar');
            this.navToggle = document.getElementById('nav-toggle');

            console.log('New navbar loaded successfully with', document.querySelectorAll('.dropdown-link').length, 'service links');
        } catch (error) {
            console.error('Error loading navbar:', error);
        }
    }

    setupEventListeners() {
        if (!this.navbar) return;

        // Mobile toggle functionality - setup after mobile nav is loaded
        this.setupMobileToggle();

        // Close mobile nav when clicking outside
        document.addEventListener('click', (e) => {
            if (this.mobileNav && 
                !this.mobileNav.contains(e.target) && 
                !this.navToggle.contains(e.target) &&
                this.mobileNav.classList.contains('active')) {
                this.closeMobileNav();
            }
        });

        // Handle window resize
        window.addEventListener('resize', () => {
            this.handleResize();
        });

        // Handle escape key
        document.addEventListener('keydown', (e) => {
            if (e.key === 'Escape') {
                this.closeMobileNav();
                this.closeAllDropdowns();
            }
        });
    }

    setupScrollHandling() {
        if (!this.navbar) return;

        window.addEventListener('scroll', () => {
            const scrollTop = window.pageYOffset || document.documentElement.scrollTop;

            // Add/remove scrolled class
            if (scrollTop > 50 && !this.isScrolled) {
                this.navbar.classList.add('scrolled');
                this.isScrolled = true;
            } else if (scrollTop <= 50 && this.isScrolled) {
                this.navbar.classList.remove('scrolled');
                this.isScrolled = false;
            }

            this.lastScrollTop = scrollTop <= 0 ? 0 : scrollTop;
        }, { passive: true });
    }

    setupDropdowns() {
        if (!this.navbar) return;

        const dropdowns = this.navbar.querySelectorAll('.nav-dropdown');
        this.dropdowns = Array.from(dropdowns);

        // Completely disable all JavaScript hover interactions
        dropdowns.forEach(dropdown => {
            const trigger = dropdown.querySelector('.dropdown-trigger');
            if (!trigger) return;

            // Remove any existing event listeners and hover classes
            dropdown.classList.remove('active');

            // Only handle mobile clicks
            trigger.addEventListener('click', (e) => {
                if (window.innerWidth < 1024) {
                    e.preventDefault();
                    this.closeAllDropdowns();
                    dropdown.classList.add('active');
                }
            });
        });

        // Close dropdowns when clicking outside
        document.addEventListener('click', (e) => {
            if (!e.target.closest('.nav-dropdown')) {
                this.closeAllDropdowns();
            }
        });
    }

    loadMobileNavigation() {
        try {
            // Remove existing mobile navigation completely
            const existingMobileNav = document.getElementById('mobile-nav-component');
            if (existingMobileNav) {
                existingMobileNav.remove();
            }

            // Create mobile navigation with correct structure inline
            const mobileNavHTML = `
<!-- Mobile Navigation Component -->
<div class="mobile-nav" id="mobile-nav">
    <div class="mobile-nav-overlay"></div>
    <div class="mobile-nav-content">
        <div class="mobile-nav-header">
            <div class="mobile-nav-logo">
                <img src="assets/logos/logo.svg" alt="Acadify Solution" class="nav-logo">
                <span class="nav-brand-text">Acadify Solution</span>
            </div>
            <button class="mobile-nav-close" id="mobile-nav-close" aria-label="Close Navigation">
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                    <line x1="18" y1="6" x2="6" y2="18"></line>
                    <line x1="6" y1="6" x2="18" y2="18"></line>
                </svg>
            </button>
        </div>

        <nav class="mobile-nav-menu">
            <ul class="mobile-nav-list">
                <li class="mobile-nav-item">
                    <a href="index.html" class="mobile-nav-link">
