/* Hero Section Component Styles */

.hero-section {
    position: relative;
    min-height: 120vh;
    display: flex;
    align-items: center;
    overflow: hidden;
    background: var(--bg-primary);
    padding: var(--spacing-4xl) 0 var(--spacing-5xl) 0;
    margin-top: 0; /* Ensure no additional margin */
}

/* Background and Animations */
.hero-background {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    z-index: 1;
}

.hero-animation-container {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    overflow: hidden;
}

.hero-bg-svg {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
}

/* Circuit Line Animations */
.circuit-path {
    stroke-dasharray: 2000;
    stroke-dashoffset: 2000;
    animation: drawPath 8s ease-in-out infinite;
}

.circuit-path:nth-child(2) {
    animation-delay: 2s;
}

.circuit-path:nth-child(3) {
    animation-delay: 4s;
}

@keyframes drawPath {
    0%, 20% {
        stroke-dashoffset: 2000;
        opacity: 0;
    }
    40%, 60% {
        stroke-dashoffset: 0;
        opacity: 0.5;
    }
    80%, 100% {
        stroke-dashoffset: -2000;
        opacity: 0;
    }
}

/* Floating Particles */
.particle {
    animation: float 6s ease-in-out infinite;
}

.particle:nth-child(1) { animation-delay: 0s; }
.particle:nth-child(2) { animation-delay: 1s; }
.particle:nth-child(3) { animation-delay: 2s; }
.particle:nth-child(4) { animation-delay: 3s; }
.particle:nth-child(5) { animation-delay: 4s; }
.particle:nth-child(6) { animation-delay: 5s; }

@keyframes float {
    0%, 100% {
        transform: translateY(0px) scale(1);
        opacity: 0.6;
    }
    50% {
        transform: translateY(-20px) scale(1.1);
        opacity: 1;
    }
}

.hero-gradient-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(ellipse at center, rgba(0, 0, 0, 0.3) 0%, rgba(0, 0, 0, 0.8) 100%);
}

/* Hero Content */
.hero-content {
    position: relative;
    z-index: 2;
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-4xl);
    align-items: center;
    min-height: calc(100vh - 160px);
}

.hero-text {
    display: flex;
    flex-direction: column;
    gap: var(--spacing-lg);
}

/* Hero Badge */
.hero-badge {
    display: inline-flex;
    align-items: center;
    gap: var(--spacing-xs);
    padding: var(--spacing-sm) var(--spacing-md);
    background: rgba(0, 191, 255, 0.1);
    border: 1px solid rgba(0, 191, 255, 0.3);
    border-radius: 50px;
    color: var(--electric-blue);
    font-size: var(--font-size-sm);
    font-weight: 500;
    width: fit-content;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
}

.hero-badge svg {
    animation: pulse 2s ease-in-out infinite;
}

@keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.6; }
}

/* Hero Title */
.hero-title {
    font-size: clamp(2.5rem, 5vw, 4rem);
    font-weight: 700;
    line-height: 1.1;
    margin: 0;
    color: var(--text-primary);
}

.title-line {
    display: block;
    opacity: 0;
    transform: translateY(30px);
}

.text-gradient {
    background: linear-gradient(135deg, var(--electric-blue), var(--neon-green));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    position: relative;
}

.text-gradient::after {
    content: '';
    position: absolute;
    bottom: -4px;
    left: 0;
    right: 0;
    height: 3px;
    background: linear-gradient(90deg, var(--electric-blue), var(--neon-green));
    border-radius: 2px;
    animation: glow 2s ease-in-out infinite alternate;
}

@keyframes glow {
    from { box-shadow: 0 0 5px var(--electric-blue); }
    to { box-shadow: 0 0 20px var(--neon-green); }
}

/* Hero Subtitle */
.hero-subtitle {
    font-size: var(--font-size-lg);
    line-height: 1.6;
    color: var(--text-secondary);
    margin: 0;
    max-width: 600px;
    opacity: 0;
    transform: translateY(30px);
}

/* Hero Stats */
.hero-stats {
    display: flex;
    gap: var(--spacing-xl);
    margin: var(--spacing-lg) 0;
    opacity: 0;
    transform: translateY(30px);
}

.stat-item {
    text-align: center;
}

.stat-number {
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--electric-blue);
    line-height: 1;
    margin-bottom: var(--spacing-xs);
}

.stat-label {
    font-size: var(--font-size-sm);
    color: var(--text-secondary);
    font-weight: 500;
}

/* Hero Actions */
.hero-actions {
    display: flex;
    gap: var(--spacing-md);
    flex-wrap: wrap;
    opacity: 0;
    transform: translateY(30px);
}

.btn {
    display: inline-flex;
    align-items: center;
    gap: var(--spacing-sm);
    padding: var(--spacing-md) var(--spacing-lg);
    border: none;
    border-radius: var(--border-radius-lg);
    font-size: var(--font-size-base);
    font-weight: 600;
    text-decoration: none;
    cursor: pointer;
    transition: all var(--transition-normal);
    position: relative;
    overflow: hidden;
}

.btn-primary {
    background: linear-gradient(135deg, var(--electric-blue), var(--neon-green));
    color: var(--text-primary);
    box-shadow: 0 4px 15px rgba(0, 191, 255, 0.3);
}

.btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(0, 191, 255, 0.5), 0 0 40px rgba(57, 255, 20, 0.3);
}

.btn-secondary {
    background: rgba(255, 255, 255, 0.1);
    color: var(--text-primary);
    border: 1px solid rgba(255, 255, 255, 0.2);
}

.btn-secondary:hover {
    background: rgba(255, 255, 255, 0.15);
    border-color: var(--electric-blue);
    color: var(--electric-blue);
    transform: translateY(-2px);
}

.btn-outline {
    background: transparent;
    color: var(--text-secondary);
    border: 2px solid rgba(255, 255, 255, 0.2);
}

.btn-outline:hover {
    background: rgba(0, 191, 255, 0.1);
    border-color: var(--electric-blue);
    color: var(--electric-blue);
}

/* Glow Button Effect */
.glow-btn::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
    transition: left 0.5s;
}

.glow-btn:hover::before {
    left: 100%;
}

/* Hero Visual */
.hero-visual {
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: 500px;
    perspective: 1000px;
}

/* Code Workspace */
.code-workspace {
    position: relative;
    width: 100%;
    max-width: 600px;
    height: 400px;
}

/* Code Window Base Styles */
.code-window {
    position: absolute;
    background: rgba(0, 0, 0, 0.95);
    border: 1px solid rgba(0, 191, 255, 0.2);
    border-radius: 12px;
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    overflow: hidden;
    box-shadow: 0 25px 50px rgba(0, 0, 0, 0.6), 0 0 30px rgba(0, 191, 255, 0.1);
}

/* Main Innovation.js Window */
.main-window {
    width: 480px;
    height: 320px;
    top: 0;
    left: 0;
    z-index: 3;
    animation: mainWindowFloat 6s ease-in-out infinite;
}

/* Terminal Window */
.terminal-window {
    width: 350px;
    height: 200px;
    top: 80px;
    right: -20px;
    z-index: 2;
    animation: terminalWindowFloat 8s ease-in-out infinite 1s;
}

/* Browser Window */
.browser-window {
    width: 400px;
    height: 250px;
    top: 150px;
    left: -30px;
    z-index: 1;
    opacity: 0.8;
    animation: browserWindowFloat 10s ease-in-out infinite 2s;
}

/* Window Headers */
.window-header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 12px 16px;
    background: rgba(255, 255, 255, 0.05);
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.terminal-header {
    background: rgba(0, 0, 0, 0.8);
}

.browser-header {
    background: rgba(30, 30, 40, 0.9);
}

/* Window Controls */
.window-controls {
    display: flex;
    gap: 8px;
}

.control {
    width: 12px;
    height: 12px;
    border-radius: 50%;
}

.control.close { background: #ff5f57; }
.control.minimize { background: #ffbd2e; }
.control.maximize { background: #28ca42; }

/* Window Titles */
.window-title {
    display: flex;
    align-items: center;
    gap: 8px;
    color: var(--text-secondary);
    font-size: 13px;
    font-family: 'Monaco', 'Menlo', 'Ubuntu Mono', monospace;
    font-weight: 500;
}

.window-actions {
    display: flex;
    align-items: center;
}

.status-indicator {
    width: 8px;
    height: 8px;
    background: #28ca42;
    border-radius: 50%;
    animation: pulse 2s ease-in-out infinite;
}

/* Main Window Content */
.window-content {
    display: flex;
    height: calc(100% - 45px);
}

.line-numbers {
    display: flex;
    flex-direction: column;
    padding: 16px 8px;
    background: rgba(0, 0, 0, 0.3);
    border-right: 1px solid rgba(255, 255, 255, 0.1);
    font-family: 'Monaco', 'Menlo', 'Ubuntu Mono', monospace;
    font-size: 12px;
    color: rgba(255, 255, 255, 0.4);
    min-width: 40px;
    text-align: right;
}

.code-area {
    flex: 1;
    padding: 16px;
    font-family: 'Monaco', 'Menlo', 'Ubuntu Mono', monospace;
    font-size: 13px;
    line-height: 1.6;
    overflow: hidden;
}

.code-line {
    margin-bottom: 4px;
    animation: codeLineReveal 0.5s ease-out forwards;
    opacity: 0;
}

.code-line:nth-child(1) { animation-delay: 0.2s; }
.code-line:nth-child(2) { animation-delay: 0.4s; }
.code-line:nth-child(3) { animation-delay: 0.6s; }
.code-line:nth-child(4) { animation-delay: 0.8s; }
.code-line:nth-child(5) { animation-delay: 1.0s; }
.code-line:nth-child(6) { animation-delay: 1.2s; }
.code-line:nth-child(7) { animation-delay: 1.4s; }
.code-line:nth-child(8) { animation-delay: 1.6s; }
.code-line:nth-child(9) { animation-delay: 1.8s; }

.code-line.blank { height: 1.6em; }
.code-line.indent { padding-left: 24px; }

/* Syntax Highlighting */
.keyword { color: #ff79c6; font-weight: 600; }
.variable { color: #8be9fd; }
.string { color: #f1fa8c; }
.function { color: #50fa7b; }
.bracket { color: #bd93f9; }
.operator { color: #ff79c6; }
.comment { color: #6272a4; font-style: italic; }

/* Terminal Content */
.terminal-content {
    padding: 16px;
    font-family: 'Monaco', 'Menlo', 'Ubuntu Mono', monospace;
    font-size: 12px;
    line-height: 1.5;
    background: linear-gradient(135deg, rgba(0, 0, 0, 0.95), rgba(10, 10, 20, 0.95));
    height: calc(100% - 45px);
}

.terminal-line {
    margin-bottom: 4px;
    animation: terminalLineReveal 0.8s ease-out forwards;
    opacity: 0;
}

.terminal-line:nth-child(1) { animation-delay: 2s; }
.terminal-line:nth-child(2) { animation-delay: 2.5s; }
.terminal-line:nth-child(3) { animation-delay: 3s; }
.terminal-line:nth-child(4) { animation-delay: 3.5s; }
.terminal-line:nth-child(5) { animation-delay: 4s; }

.prompt { color: #50fa7b; font-weight: 600; }
.command { color: #8be9fd; }
.success { color: #50fa7b; }
.cursor { 
    color: #8be9fd; 
    animation: blink 1s infinite; 
}

/* Browser Content */
.browser-bar {
    display: flex;
    align-items: center;
    flex: 1;
    margin-left: 16px;
}

.url-bar {
    display: flex;
    align-items: center;
    gap: 8px;
    background: rgba(0, 0, 0, 0.3);
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 11px;
    color: rgba(255, 255, 255, 0.7);
    font-family: 'Monaco', 'Menlo', 'Ubuntu Mono', monospace;
}

.browser-content {
    height: calc(100% - 45px);
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.05), rgba(255, 255, 255, 0.02));
}

.preview-header {
    padding: 12px 16px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.preview-nav {
    display: flex;
    gap: 16px;
}

.nav-item {
    font-size: 11px;
    color: rgba(255, 255, 255, 0.6);
    padding: 4px 8px;
    border-radius: 4px;
}

.nav-item.active {
    color: var(--electric-blue);
    background: rgba(0, 191, 255, 0.1);
}

.preview-body {
    padding: 16px;
}

.chart-placeholder {
    display: flex;
    align-items: end;
    gap: 8px;
    height: 80px;
}

.chart-bar {
    flex: 1;
    background: linear-gradient(to top, var(--electric-blue), var(--neon-green));
    border-radius: 2px 2px 0 0;
    animation: chartGrow 2s ease-out forwards;
    transform: scaleY(0);
}

.chart-bar:nth-child(1) { animation-delay: 3s; }
.chart-bar:nth-child(2) { animation-delay: 3.2s; }
.chart-bar:nth-child(3) { animation-delay: 3.4s; }
.chart-bar:nth-child(4) { animation-delay: 3.6s; }

/* Floating Tech Particles */
.tech-particles {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    pointer-events: none;
    z-index: 4;
}

.particle {
    position: absolute;
    font-size: 20px;
    animation: particleFloat 8s ease-in-out infinite;
    opacity: 0.7;
}

.particle:nth-child(1) { 
    top: 10%; 
    left: 15%; 
    animation-delay: 0s; 
}
.particle:nth-child(2) { 
    top: 70%; 
    left: 80%; 
    animation-delay: 1s; 
}
.particle:nth-child(3) { 
    top: 30%; 
    right: 10%; 
    animation-delay: 2s; 
}
.particle:nth-child(4) { 
    bottom: 20%; 
    left: 20%; 
    animation-delay: 3s; 
}
.particle:nth-child(5) { 
    top: 20%; 
    left: 70%; 
    animation-delay: 4s; 
}

/* Animations */
@keyframes mainWindowFloat {
    0%, 100% { transform: translateY(0px) rotateX(0deg); }
    50% { transform: translateY(-8px) rotateX(2deg); }
}

@keyframes terminalWindowFloat {
    0%, 100% { transform: translateY(0px) rotateX(0deg); }
    50% { transform: translateY(-6px) rotateX(-1deg); }
}

@keyframes browserWindowFloat {
    0%, 100% { transform: translateY(0px) rotateX(0deg); }
    50% { transform: translateY(-4px) rotateX(1deg); }
}

@keyframes codeLineReveal {
    from { 
        opacity: 0; 
        transform: translateX(-10px); 
    }
    to { 
        opacity: 1; 
        transform: translateX(0); 
    }
}

@keyframes terminalLineReveal {
    from { 
        opacity: 0; 
        transform: translateY(5px); 
    }
    to { 
        opacity: 1; 
        transform: translateY(0); 
    }
}

@keyframes chartGrow {
    from { transform: scaleY(0); }
    to { transform: scaleY(1); }
}

@keyframes particleFloat {
    0%, 100% { 
        transform: translateY(0px) rotate(0deg); 
        opacity: 0.7; 
    }
    25% { 
        transform: translateY(-20px) rotate(90deg); 
        opacity: 1; 
    }
    50% { 
        transform: translateY(-10px) rotate(180deg); 
        opacity: 0.5; 
    }
    75% { 
        transform: translateY(-30px) rotate(270deg); 
        opacity: 0.8; 
    }
}

@keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
}

@keyframes blink {
    0%, 50% { opacity: 1; }
    51%, 100% { opacity: 0; }
}

.tech-icon:nth-child(1) {
    top: 20%;
    right: 10%;
    animation-delay: 0s;
}

.tech-icon:nth-child(2) {
    top: 60%;
    right: 20%;
    animation-delay: 2s;
}

.tech-icon:nth-child(3) {
    bottom: 20%;
    right: 5%;
    animation-delay: 4s;
}

@keyframes floatIcon {
    0%, 100% {
        transform: translateY(0px) rotate(0deg);
        opacity: 0.6;
    }
    50% {
        transform: translateY(-15px) rotate(180deg);
        opacity: 1;
    }
}

/* Scroll Indicator */
.scroll-indicator {
    position: absolute;
    bottom: var(--spacing-xl);
    left: 50%;
    transform: translateX(-50%);
    text-align: center;
    color: var(--text-secondary);
    animation: bounce 2s ease-in-out infinite;
}

.scroll-text {
    font-size: var(--font-size-sm);
    margin-bottom: var(--spacing-xs);
}

.scroll-arrow svg {
    color: var(--electric-blue);
}

@keyframes bounce {
    0%, 20%, 50%, 80%, 100% {
        transform: translateX(-50%) translateY(0);
    }
    40% {
        transform: translateX(-50%) translateY(-10px);
    }
    60% {
        transform: translateX(-50%) translateY(-5px);
    }
}

/* Responsive Design */
@media (max-width: 1023px) {
    .hero-content {
        grid-template-columns: 1fr;
        gap: var(--spacing-2xl);
        text-align: center;
    }

    .hero-stats {
        justify-content: center;
    }

    .hero-visual {
        order: -1;
    }
}

@media (max-width: 767px) {
    .hero-section {
        padding-top: 60px;
        min-height: 100vh;
        padding-left: var(--spacing-md);
        padding-right: var(--spacing-md);
    }

    .hero-title {
        font-size: 2.5rem;
    }

    .hero-stats {
        flex-direction: column;
        gap: var(--spacing-md);
    }

    .hero-actions {
        flex-direction: column;
        width: 100%;
    }

    .btn {
        width: 100%;
        justify-content: center;
    }

    .hero-code-preview {
        max-width: 100%;
        margin: 0 var(--spacing-md);
    }

    /* Code Workspace Mobile Responsive */
    .code-workspace {
        position: relative;
        width: 100%;
        max-width: 100%;
        height: 400px;
        transform: scale(0.8);
        transform-origin: center;
        margin: 0;
        padding: 0;
    }

    .code-window {
        max-width: 280px;
        width: 280px;
        min-height: 250px;
        border-radius: 8px;
        overflow: hidden;
    }

    .main-window {
        position: relative;
        top: 20px;
        left: 10px;
        z-index: 10;
        width: 280px;
        height: 300px;
    }

    .terminal-window {
        position: absolute;
        top: 40px;
        right: 5px;
        width: 240px;
        height: 200px;
        z-index: 8;
    }

    .browser-window {
        position: absolute;
        bottom: 20px;
        left: 30px;
        width: 220px;
        height: 180px;
        z-index: 6;
        opacity: 0.9;
    }

    /* Reduce font sizes for mobile */
    .window-title {
        font-size: 11px;
    }

    .code-area {
        padding: 12px;
        font-size: 10px;
        line-height: 1.4;
    }

    .line-numbers {
        padding: 12px 6px;
        font-size: 10px;
        min-width: 30px;
    }

    .terminal-content {
        padding: 12px;
        font-size: 10px;
        line-height: 1.4;
    }

    .browser-content {
        padding: 8px;
        font-size: 9px;
    }

    /* Hide some floating particles on mobile for performance */
    .tech-particles .particle:nth-child(n+4) {
        display: none;
    }

    /* Adjust floating tech icons for mobile */
    .tech-icon {
        font-size: 16px;
    }
}

/* Animation Classes (for JavaScript) */
.animate-in {
    opacity: 1 !important;
    transform: translateY(0) !important;
    transition: all 0.8s ease-out;
}

.animate-in.delay-1 { transition-delay: 0.2s; }
.animate-in.delay-2 { transition-delay: 0.4s; }
.animate-in.delay-3 { transition-delay: 0.6s; }
.animate-in.delay-4 { transition-delay: 0.8s; }
.animate-in.delay-5 { transition-delay: 1s; }

src/components/services/services.css.content:
/* Services Section Component Styles */

.services-section {
    padding: var(--spacing-5xl) 0;
    background: var(--bg-primary);
    position: relative;
    overflow: hidden;
}

.services-section::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(circle at 20% 50%, rgba(0, 191, 255, 0.05) 0%, transparent 50%),
                radial-gradient(circle at 80% 50%, rgba(57, 255, 20, 0.05) 0%, transparent 50%);
    pointer-events: none;
}

/* Section Header */
.section-header {
    text-align: center;
    margin-bottom: var(--spacing-4xl);
    position: relative;
    z-index: 2;
}

.section-badge {
    display: inline-flex;
    align-items: center;
    gap: var(--spacing-xs);
    padding: var(--spacing-sm) var(--spacing-md);
    background: rgba(0, 191, 255, 0.1);
    border: 1px solid rgba(0, 191, 255, 0.3);
    border-radius: 50px;
    color: var(--electric-blue);
    font-size: var(--font-size-sm);
    font-weight: 500;
    margin-bottom: var(--spacing-lg);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
}

.section-title {
    font-size: clamp(2rem, 4vw, 3rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--text-primary);
    margin-bottom: var(--spacing-md);
}

.section-subtitle {
    font-size: var(--font-size-lg);
    color: var(--text-secondary);
    max-width: 600px;
    margin: 0 auto;
    line-height: 1.6;
}

/* Services Grid */
.services-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: var(--spacing-xl);
    margin-bottom: var(--spacing-4xl);
}

/* Service Card */
.service-card {
    background: rgba(0, 0, 0, 0.4);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: var(--border-radius-xl);
    padding: var(--spacing-2xl);
    position: relative;
    overflow: hidden;
    transition: all var(--transition-normal);
    cursor: pointer;
    group: hover;
}

.service-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(0, 191, 255, 0.05), rgba(57, 255, 20, 0.05));
    opacity: 0;
    transition: opacity var(--transition-normal);
}

.service-card:hover::before {
    opacity: 1;
}

.service-card:hover {
    transform: translateY(-8px);
    border-color: rgba(0, 191, 255, 0.3);
    box-shadow: 0 20px 40px rgba(0, 191, 255, 0.2), 
                0 0 60px rgba(0, 191, 255, 0.1);
}

/* Service Icon */
.service-icon {
    width: 80px;
    height: 80px;
    background: linear-gradient(135deg, rgba(0, 191, 255, 0.1), rgba(57, 255, 20, 0.1));
    border: 2px solid rgba(0, 191, 255, 0.3);
    border-radius: var(--border-radius-lg);
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: var(--spacing-lg);
    position: relative;
    overflow: hidden;
    transition: all var(--transition-normal);
}

.service-icon::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
    transition: left 0.6s;
}

.service-card:hover .service-icon::before {
    left: 100%;
}

.service-icon svg {
    color: var(--electric-blue);
    transition: all var(--transition-normal);
}

.service-card:hover .service-icon {
    background: linear-gradient(135deg, rgba(0, 191, 255, 0.2), rgba(57, 255, 20, 0.2));
    border-color: var(--electric-blue);
    transform: scale(1.05);
}

.service-card:hover .service-icon svg {
    transform: scale(1.1);
    filter: drop-shadow(0 0 10px var(--electric-blue));
}

/* Service Content */
.service-title {
    font-size: var(--font-size-xl);
    font-weight: 600;
    color: var(--text-primary);
    margin-bottom: var(--spacing-md);
    transition: color var(--transition-normal);
}

.service-card:hover .service-title {
    color: var(--electric-blue);
}

.service-description {
    color: var(--text-secondary);
    line-height: 1.6;
    margin-bottom: var(--spacing-lg);
    font-size: var(--font-size-base);
}

/* Service Features */
.service-features {
    list-style: none;
    padding: 0;
    margin: 0 0 var(--spacing-lg) 0;
}

.service-features li {
    position: relative;
    padding: var(--spacing-xs) 0;
    padding-left: var(--spacing-lg);
    color: var(--text-secondary);
    font-size: var(--font-size-sm);
    transition: color var(--transition-fast);
}

.service-features li::before {
    content: '';
    position: absolute;
    left: 0;
    top: 50%;
    transform: translateY(-50%);
    width: 8px;
    height: 8px;
    background: var(--electric-blue);
    border-radius: 50%;
    box-shadow: 0 0 10px rgba(0, 191, 255, 0.5);
}

.service-card:hover .service-features li {
    color: var(--text-primary);
}

.service-features li:nth-child(1)::before { animation-delay: 0.1s; }
.service-features li:nth-child(2)::before { animation-delay: 0.2s; }
.service-features li:nth-child(3)::before { animation-delay: 0.3s; }
.service-features li:nth-child(4)::before { animation-delay: 0.4s; }

.service-card:hover .service-features li::before {
    animation: pulse 0.6s ease-out;
}

@keyframes pulse {
    0% { transform: translateY(-50%) scale(1); }
    50% { transform: translateY(-50%) scale(1.5); box-shadow: 0 0 20px rgba(0, 191, 255, 0.8); }
    100% { transform: translateY(-50%) scale(1); }
}

/* Service Action */
.service-action {
    position: relative;
}

.service-link {
    display: inline-flex;
    align-items: center;
    gap: var(--spacing-xs);
    color: var(--electric-blue);
    text-decoration: none;
    font-weight: 500;
    font-size: var(--font-size-sm);
    padding: var(--spacing-sm) 0;
    position: relative;
    transition: all var(--transition-normal);
}

.service-link::after {
    content: '';
    position: absolute;
    bottom: var(--spacing-xs);
    left: 0;
    width: 0;
    height: 2px;
    background: linear-gradient(90deg, var(--electric-blue), var(--neon-green));
    transition: width var(--transition-normal);
}

.service-link:hover::after,
.service-card:hover .service-link::after {
    width: 100%;
}

.service-link svg {
    transition: transform var(--transition-fast);
}

.service-link:hover svg,
.service-card:hover .service-link svg {
    transform: translate(4px, -4px);
}

/* Services CTA */
.services-cta {
    text-align: center;
    padding: var(--spacing-2xl);
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: var(--border-radius-xl);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    position: relative;
    overflow: hidden;
}

.services-cta::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(0, 191, 255, 0.05), rgba(57, 255, 20, 0.05));
    opacity: 0.5;
}

.services-cta h3 {
    font-size: var(--font-size-2xl);
    font-weight: 600;
    color: var(--text-primary);
    margin-bottom: var(--spacing-md);
    position: relative;
    z-index: 2;
}

.services-cta p {
    color: var(--text-secondary);
    font-size: var(--font-size-lg);
    margin-bottom: var(--spacing-xl);
    max-width: 500px;
    margin-left: auto;
    margin-right: auto;
    position: relative;
    z-index: 2;
}

.cta-actions {
    display: flex;
    gap: var(--spacing-md);
    justify-content: center;
    flex-wrap: wrap;
    position: relative;
    z-index: 2;
}

/* Animation on Scroll */
.service-card {
    opacity: 0;
    transform: translateY(30px);
    transition: all 0.6s ease-out;
}

.service-card.animate-in {
    opacity: 1;
    transform: translateY(0);
}

.service-card:nth-child(1) { transition-delay: 0.1s; }
.service-card:nth-child(2) { transition-delay: 0.2s; }
.service-card:nth-child(3) { transition-delay: 0.3s; }
.service-card:nth-child(4) { transition-delay: 0.4s; }
.service-card:nth-child(5) { transition-delay: 0.5s; }
.service-card:nth-child(6) { transition-delay: 0.6s; }

/* Responsive Design */
@media (max-width: 1023px) {
    .services-grid {
        grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
        gap: var(--spacing-lg);
    }
}

@media (max-width: 767px) {
    .services-section {
        padding: var(--spacing-4xl) 0;
    }

    .services-grid {
        grid-template-columns: 1fr;
        gap: var(--spacing-md);
    }

    .service-card {
        padding: var(--spacing-xl);
    }

    .service-icon {
        width: 60px;
        height: 60px;
    }

    .service-icon svg {
        width: 24px;
        height: 24px;
    }

    .cta-actions {
        flex-direction: column;
        align-items: center;
    }

    .cta-actions .btn {
        width: 100%;
        max-width: 300px;
    }
}

@media (max-width: 480px) {
    .services-grid {
        grid-template-columns: 1fr;
    }

    .service-card {
        padding: var(--spacing-lg);
    }

    .services-cta {
        padding: var(--spacing-lg);
    }
}

src/components/tech-stack/tech-stack.css.content:
/* Tech Stack Section - Modern Glassmorphism Card Design */
.tech-stack-section {
    padding: 120px 0;
    background: linear-gradient(135deg, rgba(0, 0, 0, 0.95) 0%, rgba(20, 20, 20, 0.95) 100%);
    position: relative;
    overflow: hidden;
}

.tech-stack-section::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(ellipse at top center, rgba(0, 191, 255, 0.1) 0%, transparent 70%);
    pointer-events: none;
}

/* Section Header */
.tech-stack-section .section-header {
    text-align: center;
    margin-bottom: 80px;
    position: relative;
    z-index: 2;
}

.tech-stack-section .section-badge {
    display: inline-flex;
    align-items: center;
    gap: 8px;
    background: linear-gradient(135deg, rgba(0, 191, 255, 0.1) 0%, rgba(57, 255, 20, 0.05) 100%);
    border: 1px solid rgba(0, 191, 255, 0.2);
    padding: 8px 16px;
    border-radius: 50px;
    margin-bottom: 24px;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
}

.tech-stack-section .section-badge svg {
    color: #00BFFF;
}

.tech-stack-section .section-badge span {
    color: #FFFFFF;
    font-size: 14px;
    font-weight: 500;
}

.tech-stack-section .section-title {
    font-size: clamp(32px, 4vw, 56px);
    font-weight: 700;
    color: #FFFFFF;
    margin-bottom: 24px;
    line-height: 1.2;
}

.tech-stack-section .text-gradient {
    background: linear-gradient(135deg, #00BFFF 0%, #39FF14 50%, #FFA500 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.tech-stack-section .section-subtitle {
    font-size: 18px;
    color: rgba(255, 255, 255, 0.7);
    max-width: 600px;
    margin: 0 auto;
    line-height: 1.6;
}

/* Controls */
.tech-controls {
    display: flex;
    flex-direction: column;
    gap: 24px;
    margin-bottom: 60px;
    align-items: center;
}

.search-container {
    position: relative;
    max-width: 400px;
    width: 100%;
}

.search-icon {
    position: absolute;
    left: 16px;
    top: 50%;
    transform: translateY(-50%);
    color: rgba(255, 255, 255, 0.5);
    z-index: 2;
}

.tech-search-input {
    width: 100%;
    padding: 16px 16px 16px 48px;
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 50px;
    color: #FFFFFF;
    font-size: 16px;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    transition: all 0.3s ease;
}

.tech-search-input:focus {
    outline: none;
    border-color: #00BFFF;
    background: rgba(255, 255, 255, 0.08);
    box-shadow: 0 0 0 3px rgba(0, 191, 255, 0.1);
}

.tech-search-input::placeholder {
    color: rgba(255, 255, 255, 0.5);
}

.filter-tabs {
    display: flex;
    gap: 8px;
    flex-wrap: wrap;
    justify-content: center;
}

.filter-tab {
    padding: 12px 24px;
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 50px;
    color: rgba(255, 255, 255, 0.7);
    font-size: 14px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
}

.filter-tab:hover {
    background: rgba(255, 255, 255, 0.08);
    color: #FFFFFF;
    transform: translateY(-2px);
}

.filter-tab.active {
    background: linear-gradient(135deg, #00BFFF 0%, #39FF14 100%);
    border-color: #00BFFF;
    color: #000000;
    font-weight: 600;
}

/* Tech Grid */
.tech-grid-container {
    position: relative;
    z-index: 2;
}

.tech-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
    gap: 24px;
    max-width: 1400px;
    margin: 0 auto;
}

/* Tech Cards */
.tech-card {
    position: relative;
    background: rgba(255, 255, 255, 0.03);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 20px;
    padding: 32px 24px;
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    overflow: hidden;
    cursor: pointer;
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    min-height: 240px;
}

.tech-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.1) 0%, rgba(255, 255, 255, 0.05) 100%);
    opacity: 0;
    transition: opacity 0.3s ease;
    border-radius: 20px;
}

.tech-card:hover::before {
    opacity: 1;
}

.tech-card:hover {
    transform: translateY(-8px) scale(1.02);
    border-color: rgba(0, 191, 255, 0.3);
    box-shadow: 
        0 20px 40px rgba(0, 0, 0, 0.3),
        0 0 0 1px rgba(0, 191, 255, 0.1),
        inset 0 1px 0 rgba(255, 255, 255, 0.1);
}

/* Tech Glow Effects */
.tech-glow {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 100px;
    height: 100px;
    border-radius: 50%;
    opacity: 0;
    transition: opacity 0.3s ease;
    pointer-events: none;
    z-index: 1;
}

.tech-card:hover .tech-glow {
    opacity: 0.3;
}

.frontend-glow {
    background: radial-gradient(circle, #61DAFB 0%, transparent 70%);
}

.backend-glow {
    background: radial-gradient(circle, #339933 0%, transparent 70%);
}

.database-glow {
    background: radial-gradient(circle, #336791 0%, transparent 70%);
}

.cloud-glow {
    background: radial-gradient(circle, #FF9900 0%, transparent 70%);
}

.mobile-glow {
    background: radial-gradient(circle, #02569B 0%, transparent 70%);
}

.ai-glow {
    background: radial-gradient(circle, #FF6F00 0%, transparent 70%);
}

/* Tech Icon */
.tech-icon {
    position: relative;
    z-index: 3;
    margin-bottom: 20px;
    padding: 16px;
    background: rgba(255, 255, 255, 0.05);
    border-radius: 16px;
    transition: all 0.3s ease;
}

.tech-card:hover .tech-icon {
    transform: scale(1.1) rotate(5deg);
    background: rgba(255, 255, 255, 0.08);
}

.tech-icon svg {
    display: block;
    transition: all 0.3s ease;
}

/* Tech Content */
.tech-content {
    position: relative;
    z-index: 3;
    flex: 1;
    display: flex;
    flex-direction: column;
    justify-content: center;
}

.tech-name {
    font-size: 20px;
    font-weight: 600;
    color: #FFFFFF;
    margin-bottom: 8px;
    transition: color 0.3s ease;
}

.tech-card:hover .tech-name {
    color: #00BFFF;
}

.tech-description {
    font-size: 14px;
    color: rgba(255, 255, 255, 0.6);
    line-height: 1.4;
    margin-bottom: 20px;
}

/* Category Badges */
.tech-category-badge {
    position: absolute;
    top: 16px;
    right: 16px;
    padding: 4px 12px;
    border-radius: 20px;
    font-size: 12px;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    z-index: 3;
}

.tech-category-badge.frontend {
    background: rgba(97, 218, 251, 0.2);
    color: #61DAFB;
    border: 1px solid rgba(97, 218, 251, 0.3);
}

.tech-category-badge.backend {
    background: rgba(51, 153, 51, 0.2);
    color: #339933;
    border: 1px solid rgba(51, 153, 51, 0.3);
}

.tech-category-badge.database {
    background: rgba(51, 103, 145, 0.2);
    color: #336791;
    border: 1px solid rgba(51, 103, 145, 0.3);
}

.tech-category-badge.cloud {
    background: rgba(255, 153, 0, 0.2);
    color: #FF9900;
    border: 1px solid rgba(255, 153, 0, 0.3);
}

.tech-category-badge.mobile {
    background: rgba(2, 86, 155, 0.2);
    color: #02569B;
    border: 1px solid rgba(2, 86, 155, 0.3);
}

.tech-category-badge.ai {
    background: rgba(255, 111, 0, 0.2);
    color: #FF6F00;
    border: 1px solid rgba(255, 111, 0, 0.3);
}

/* Filter Animation */
.tech-card.hidden {
    opacity: 0;
    transform: scale(0.8);
    pointer-events: none;
}

.tech-card.visible {
    opacity: 1;
    transform: scale(1);
}

/* Responsive Design */
@media (max-width: 768px) {
    .tech-stack-section {
        padding: 80px 0;
    }

    .tech-controls {
        gap: 16px;
        margin-bottom: 40px;
    }

    .search-container {
        max-width: 100%;
    }

    .filter-tabs {
        gap: 6px;
    }

    .filter-tab {
        padding: 10px 16px;
        font-size: 13px;
    }

    .tech-grid {
        grid-template-columns: 1fr;
        gap: 16px;
        padding: 0 16px;
    }

    .tech-card {
        padding: 24px 20px;
        min-height: 200px;
    }

    .tech-name {
        font-size: 18px;
    }

    .tech-description {
        font-size: 13px;
    }

    .tech-icon {
        padding: 12px;
    }

    .tech-icon svg {
        width: 28px;
        height: 28px;
    }
}

@media (max-width: 480px) {
    .tech-stack-section .section-title {
        font-size: 28px;
    }

    .tech-stack-section .section-subtitle {
        font-size: 16px;
    }

    .filter-tabs {
        justify-content: flex-start;
        overflow-x: auto;
        padding-bottom: 8px;
        scrollbar-width: none;
        -ms-overflow-style: none;
    }

    .filter-tabs::-webkit-scrollbar {
        display: none;
    }

    .filter-tab {
        white-space: nowrap;
        flex-shrink: 0;
    }

    .tech-card {
        padding: 20px 16px;
        min-height: 180px;
    }
}

/* Animation for page load */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.tech-card {
    animation: fadeInUp 0.6s ease forwards;
}

.tech-card:nth-child(1) { animation-delay: 0.1s; }
.tech-card:nth-child(2) { animation-delay: 0.2s; }
.tech-card:nth-child(3) { animation-delay: 0.3s; }
.tech-card:nth-child(4) { animation-delay: 0.4s; }
.tech-card:nth-child(5) { animation-delay: 0.5s; }
.tech-card:nth-child(6) { animation-delay: 0.6s; }

/* Performance optimizations */
.tech-card {
    will-change: transform;
}

.tech-glow {
    will-change: opacity;
}

.tech-icon svg {
    will-change: transform;
}

src/components/tech-stack/tech-stack.js.content:
// Simplified Tech Stack Filtering System
document.addEventListener('DOMContentLoaded', function() {
    console.log('🔧 Initializing Tech Stack Filtering...');

    // Wait for tech stack component to load
    const initFiltering = () => {
        const searchInput = document.getElementById('tech-search');
        const filterTabs = document.querySelectorAll('.filter-tab');
        const techCards = document.querySelectorAll('.tech-card');

        console.log(`Found ${filterTabs.length} filter tabs and ${techCards.length} tech cards`);

        if (!searchInput || filterTabs.length === 0 || techCards.length === 0) {
            console.log('⏳ Waiting for tech stack elements to load...');
            setTimeout(initFiltering, 500);
            return;
        }

        let currentFilter = 'all';
        let currentSearch = '';

        // Search functionality
        searchInput.addEventListener('input', function(e) {
            currentSearch = e.target.value.toLowerCase().trim();
            console.log('🔍 Search:', currentSearch);
            applyFilters();
        });

        // Filter tab functionality
        filterTabs.forEach(tab => {
            tab.addEventListener('click', function() {
                // Remove active from all tabs
                filterTabs.forEach(t => t.classList.remove('active'));
                // Add active to clicked tab
                this.classList.add('active');

                currentFilter = this.dataset.category;
                console.log('🏷️ Filter:', currentFilter);
                applyFilters();
            });
        });

        function applyFilters() {
            let visibleCount = 0;

            techCards.forEach((card, index) => {
                const category = card.dataset.category;
                const techName = card.dataset.tech || '';
                const cardText = card.textContent.toLowerCase();

                // Check category filter
                const categoryMatch = currentFilter === 'all' || category === currentFilter;

                // Check search filter
                const searchMatch = !currentSearch || 
                    cardText.includes(currentSearch) ||
                    techName.includes(currentSearch) ||
                    category.includes(currentSearch);

                const shouldShow = categoryMatch && searchMatch;

                if (shouldShow) {
                    card.style.display = 'flex';
                    card.style.opacity = '1';
                    card.style.transform = 'translateY(0)';
                    visibleCount++;
                } else {
                    card.style.display = 'none';
                }
            });

            console.log(`📊 Showing ${visibleCount}/${techCards.length} cards`);

            // Show/hide no results message
            updateNoResultsMessage(visibleCount === 0);
        }

        function updateNoResultsMessage(show) {
            let noResults = document.querySelector('.no-results-message');

            if (show && !noResults) {
                noResults = document.createElement('div');
                noResults.className = 'no-results-message';
                noResults.style.cssText = `
                    grid-column: 1 / -1;
                    text-align: center;
                    padding: 60px 20px;
                    color: rgba(255, 255, 255, 0.6);
                    font-size: 18px;
                `;
                noResults.innerHTML = `
                    <div style="margin-bottom: 16px;">
                        <svg width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" style="opacity: 0.5;">
                            <circle cx="11" cy="11" r="8"></circle>
                            <path d="m21 21-4.35-4.35"></path>
                        </svg>
                    </div>
                    <h3 style="color: rgba(255, 255, 255, 0.8); margin-bottom: 8px;">No technologies found</h3>
                    <p>Try adjusting your search or filter criteria</p>
                `;

                const techGrid = document.getElementById('tech-grid');
                if (techGrid) {
                    techGrid.appendChild(noResults);
                }
            }

            if (noResults) {
                noResults.style.display = show ? 'block' : 'none';
            }
        }

        // Initial filter application
        applyFilters();

        console.log('✅ Tech Stack filtering initialized successfully');
    };

    // Start initialization
    initFiltering();
});

src/components/testimonials/testimonials.content:
/* Client Success Component Styles */

.testimonials-section {
    padding: var(--spacing-5xl) 0;
    background: var(--bg-primary);
    position: relative;
    overflow: visible;
    display: block !important;
    visibility: visible !important;
    opacity: 1 !important;
    min-height: 600px;
    width: 100%;
    z-index: 1;
}

.testimonials-section * {
    visibility: visible !important;
    opacity: 1 !important;
}

.testimonials-section::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
        radial-gradient(circle at 80% 20%, rgba(0, 191, 255, 0.04) 0%, transparent 50%),
        radial-gradient(circle at 20% 80%, rgba(57, 255, 20, 0.04) 0%, transparent 50%),
        radial-gradient(circle at 50% 50%, rgba(255, 165, 0, 0.02) 0%, transparent 70%);
    pointer-events: none;
}

/* Success Metrics Section */
.success-metrics {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: var(--spacing-xl);
    margin: var(--spacing-4xl) 0;
    padding: var(--spacing-2xl);
    background: rgba(0, 0, 0, 0.3);
    border-radius: var(--border-radius-xl);
    border: 1px solid rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
}

.metric-item {
    text-align: center;
    padding: var(--spacing-lg);
}

.metric-icon {
    margin: 0 auto var(--spacing-md);
    width: 48px;
    height: 48px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: linear-gradient(135deg, rgba(0, 191, 255, 0.2), rgba(57, 255, 20, 0.2));
    border-radius: 50%;
    color: var(--text-primary);
}

.metric-number {
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--accent-blue);
    line-height: 1;
    display: inline-block;
}

.metric-suffix {
    font-size: 1.5rem;
    font-weight: 600;
    color: var(--accent-green);
    margin-left: 2px;
}

.metric-label {
    font-size: 0.9rem;
    color: var(--text-secondary);
    margin-top: var(--spacing-xs);
    font-weight: 500;
}

/* Testimonials Slider */
.testimonials-slider {
    position: relative;
    margin-bottom: var(--spacing-4xl);
    overflow: hidden;
    border-radius: var(--border-radius-xl);
    width: 100%;
    min-height: 350px;
    display: block;
    visibility: visible;
    opacity: 1;
    z-index: 1;
}

.testimonials-track {
    display: flex !important;
    width: 4000%; /* 40 testimonials for seamless loop (20 original + 20 duplicate) */
    animation: testimonialLoop 60s linear infinite;
    height: 100%;
    align-items: stretch;
    will-change: transform;
    visibility: visible !important;
    opacity: 1 !important;
}

.testimonials-slider:hover .testimonials-track {
    animation-play-state: paused;
}

@keyframes testimonialLoop {
    0% { transform: translateX(0); }
    100% { transform: translateX(-50%); } /* Move by 50% to loop seamlessly */
}

.testimonial-card {
    flex: 0 0 2.5%; /* Each card takes 2.5% of track width to show multiple cards */
    padding: 0 var(--spacing-md);
    box-sizing: border-box;
    max-width: 350px; /* Limit maximum width similar to service cards */
    min-width: 300px;
}

/* Navigation Arrows */
.slider-arrow {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    width: 50px;
    height: 50px;
    background: rgba(0, 0, 0, 0.7);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 50%;
    color: var(--text-primary);
    cursor: pointer;
    z-index: 10;
    transition: all var(--transition-medium);
    display: flex;
    align-items: center;
    justify-content: center;
}

.slider-arrow:hover {
    background: rgba(0, 191, 255, 0.2);
    border-color: var(--accent-blue);
    color: var(--accent-blue);
    transform: translateY(-50%) scale(1.1);
    box-shadow: 0 5px 20px rgba(0, 191, 255, 0.3);
}

.slider-arrow-left {
    left: 20px;
}

.slider-arrow-right {
    right: 20px;
}

/* Testimonial Card - Services-style design */
.testimonial-card {
    background: rgba(0, 0, 0, 0.6);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: var(--border-radius-xl);
    padding: var(--spacing-xl) var(--spacing-lg);
    position: relative;
    overflow: hidden;
    transition: all var(--transition-medium);
    height: 300px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    margin: 0 var(--spacing-md);
    width: 100%;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
    opacity: 1;
    visibility: visible;
}

.testimonial-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(0, 191, 255, 0.05), rgba(57, 255, 20, 0.05));
    opacity: 0;
    transition: opacity var(--transition-medium);
}

.testimonial-card:hover {
    transform: translateY(-8px);
    border-color: rgba(0, 191, 255, 0.4);
    box-shadow: 0 20px 40px rgba(0, 191, 255, 0.2), 
                0 8px 32px rgba(0, 0, 0, 0.4);
}

.testimonial-card:hover::before {
    opacity: 1;
}

/* Testimonial Header */
.testimonial-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: var(--spacing-xl);
    position: relative;
    z-index: 2;
}

.testimonial-icon {
    width: 40px;
    height: 40px;
    background: linear-gradient(135deg, rgba(0, 191, 255, 0.2), rgba(57, 255, 20, 0.2));
    border-radius: 10px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--accent-blue);
    transition: all var(--transition-medium);
}

.testimonial-icon svg {
    width: 20px;
    height: 20px;
}

/* Fix SVG sizes in testimonials */
.testimonials-track svg[width="24"] {
    width: 20px !important;
    height: 20px !important;
}

.testimonial-card:hover .testimonial-icon {
    background: linear-gradient(135deg, rgba(0, 191, 255, 0.3), rgba(57, 255, 20, 0.3));
    transform: scale(1.1);
}

/* Testimonial Content */
.testimonial-content {
    position: relative;
    z-index: 2;
    flex: 1;
}

.testimonial-title {
    font-size: 1rem;
    font-weight: 600;
    color: var(--text-primary);
    margin-bottom: var(--spacing-sm);
    line-height: 1.3;
}

.testimonial-text {
    font-size: 0.9rem;
    line-height: 1.6;
    color: var(--text-secondary);
    margin-bottom: var(--spacing-lg);
}

/* Testimonial Metrics */
.testimonial-metrics {
    display: flex;
    align-items: center;
    gap: var(--spacing-sm);
    padding: var(--spacing-sm) var(--spacing-md);
    background: rgba(0, 191, 255, 0.15);
    border-radius: var(--border-radius-md);
    border-left: 4px solid var(--accent-blue);
    margin-bottom: var(--spacing-lg);
    backdrop-filter: blur(10px);
}

.metric-highlight {
    font-size: 1rem;
    font-weight: 700;
    color: var(--accent-blue);
    line-height: 1;
}

.metric-label {
    font-size: 0.7rem;
    color: var(--text-secondary);
    font-weight: 500;
}

/* Testimonial Rating */
.testimonial-rating {
    display: flex;
    gap: 4px;
    margin-top: var(--spacing-md);
}

.testimonial-rating svg {
    color: var(--accent-orange);
    width: 18px;
    height: 18px;
}

/* Testimonial Author */
.testimonial-author {
    display: flex;
    align-items: center;
    gap: var(--spacing-md);
    position: relative;
    z-index: 2;
    padding-top: var(--spacing-sm);
    border-top: 1px solid rgba(255, 255, 255, 0.1);
}

.author-avatar {
    width: 36px;
    height: 36px;
    border-radius: 50%;
    background: linear-gradient(135deg, var(--accent-blue), var(--accent-green));
    display: flex;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
    font-weight: 600;
    font-size: 0.8rem;
    color: var(--bg-primary);
    border: 2px solid rgba(255, 255, 255, 0.1);
}

.author-info {
    flex: 1;
}

.author-name {
    font-size: 0.85rem;
    font-weight: 600;
    color: var(--text-primary);
    margin: 0 0 2px 0;
    line-height: 1.2;
}

.author-company {
    font-size: 0.75rem;
    color: var(--text-secondary);
    margin: 0;
    line-height: 1.2;
}

/* Mobile Responsiveness */
@media (max-width: 768px) {
    .success-metrics {
        grid-template-columns: repeat(2, 1fr);
        gap: var(--spacing-md);
        padding: var(--spacing-lg);
    }

    .metric-item {
        padding: var(--spacing-md);
    }

    .metric-number {
        font-size: 2rem;
    }

    .testimonials-track {
        /* Keep animation on mobile for continuous scrolling */
        animation: testimonialLoop 35s linear infinite;
    }

    .testimonial-card {
        /* Keep horizontal scrolling even on mobile for seamless experience */
        flex: 0 0 80%;  /* Make cards slightly smaller on mobile */
        margin-bottom: 0;
        max-width: 300px;
    }

    .slider-arrow {
        display: none;
    }

    .metric-value {
        font-size: 1.3rem;
    }

    .author-avatar {
        width: 48px;
        height: 48px;
    }

    .avatar-image {
        font-size: 1rem;
    }

    .case-study-tag {
        position: static;
        margin-bottom: var(--spacing-md);
        text-align: center;
    }
}

@media (max-width: 480px) {
    .success-metrics {
        grid-template-columns: 1fr;
    }

    .testimonial-text {
        font-size: 1rem;
    }

    .testimonial-card {
        margin: 0;
    }
}

/* Slider Dots */
.slider-dots {
    display: flex;
    justify-content: center;
    gap: var(--spacing-md);
    margin-top: var(--spacing-xl);
}

.slider-dot {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.3);
    border: none;
    cursor: pointer;
    transition: all var(--transition-medium);
}

.slider-dot.active {
    background: var(--accent-blue);
    box-shadow: 0 0 10px rgba(0, 191, 255, 0.5);
    transform: scale(1.2);
}

.slider-dot:hover {
    background: var(--accent-green);
    transform: scale(1.1);
}

/* Client Logos Section */
.client-logos-section {
    margin-top: var(--spacing-4xl);
    padding-top: var(--spacing-2xl);
    border-top: 1px solid rgba(255, 255, 255, 0.1);
}

.client-logos-title {
    text-align: center;
    font-size: var(--font-size-lg);
    color: var(--text-secondary);
    margin-bottom: var(--spacing-xl);
    font-weight: 500;
}

.client-logos-container {
    overflow: hidden;
    position: relative;
}

.client-logos-track {
    display: flex;
    gap: var(--spacing-2xl);
    animation: scrollLogos 30s linear infinite;
}

.client-logo {
    flex-shrink: 0;
    width: 150px;
    height: 60px;
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0.6;
    transition: opacity var(--transition-normal);
}

.client-logo:hover {
    opacity: 1;
}

.client-logo svg {
    max-width: 100%;
    max-height: 100%;
    color: var(--text-secondary);
    transition: color var(--transition-normal);
}

.client-logo:hover svg {
    color: var(--electric-blue);
}

@keyframes scrollLogos {
    0% {
        transform: translateX(0);
    }
    100% {
        transform: translateX(-50%);
    }
}

/* Slider States */
.testimonials-track[data-slide="0"] { transform: translateX(0); }
.testimonials-track[data-slide="1"] { transform: translateX(-100%); }
.testimonials-track[data-slide="2"] { transform: translateX(-200%); }
.testimonials-track[data-slide="3"] { transform: translateX(-300%); }

/* Animation on Scroll */
.testimonial-card {
    opacity: 0;
    transform: translateY(30px);
    transition: all 0.8s ease-out;
}

.testimonial-card.animate-in {
    opacity: 1;
    transform: translateY(0);
}

/* Responsive Design */
@media (max-width: 1023px) {
    .testimonials-section {
        padding: var(--spacing-4xl) 0;
    }

    .testimonial-card {
        padding: var(--spacing-xl);
    }

    .testimonial-text {
        font-size: var(--font-size-base);
    }

    .author-avatar {
        width: 50px;
        height: 50px;
    }
}

@media (max-width: 767px) {
    .testimonial-card {
        padding: var(--spacing-lg);
        margin: 0 var(--spacing-md);
    }

    .testimonial-text {
        font-size: var(--font-size-base);
        line-height: 1.6;
    }

    .testimonial-author {
        flex-direction: column;
        text-align: center;
        gap: var(--spacing-sm);
    }

    .slider-nav {
        gap: var(--spacing-md);
    }

    .slider-btn {
        width: 40px;
        height: 40px;
    }

    .client-logos-track {
        gap: var(--spacing-xl);
    }

    .client-logo {
        width: 120px;
        height: 50px;
    }
}

@media (max-width: 480px) {
    .testimonial-card {
        padding: var(--spacing-md);
    }

    .testimonial-text::before,
    .testimonial-text::after {
        font-size: 2em;
    }

    .quote-icon svg {
        width: 24px;
        height: 24px;
    }

    .client-logos-track {
        gap: var(--spacing-lg);
    }

    .client-logo {
        width: 100px;
        height: 40px;
    }

    .client-logos-title {
        font-size: var(--font-size-base);
    }
}

/* Auto-play pause on hover */
.testimonials-slider:hover .testimonials-track {
    animation-play-state: paused;
}

.client-logos-container:hover .client-logos-track {
    animation-play-state: paused;
}

/* Focus styles for accessibility */
.slider-btn:focus,
.slider-dot:focus {
    outline: 2px solid var(--electric-blue);
    outline-offset: 2px;
}

/* Print styles */
@media print {
    .testimonials-section {
        background: white;
        color: black;
    }

    .testimonial-card {
        background: white;
        border: 1px solid #ccc;
        break-inside: avoid;
    }

    .slider-nav,
    .client-logos-section {
        display: none;
    }
}

src/styles/pages/home.css.content:
/* Page-specific Styles */

/* Home Page Styles */
.hero-section {
    min-height: 100vh;
    display: flex;
    align-items: center;
    background: radial-gradient(circle at 30% 50%, rgba(0, 191, 255, 0.15) 0%, transparent 50%),
                radial-gradient(circle at 70% 30%, rgba(57, 255, 20, 0.1) 0%, transparent 50%),
                radial-gradient(circle at 20% 80%, rgba(255, 165, 0, 0.05) 0%, transparent 50%);
    position: relative;
    overflow: hidden;
}

.hero-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-4xl);
    align-items: center;
    position: relative;
    z-index: 2;
}

.hero-text h1 {
    font-size: var(--font-size-6xl);
    font-weight: 700;
    line-height: var(--leading-tight);
    margin-bottom: var(--spacing-lg);
    background: var(--gradient-primary);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.hero-text .subtitle {
    font-size: var(--font-size-xl);
    color: var(--text-secondary);
    margin-bottom: var(--spacing-2xl);
    line-height: var(--leading-relaxed);
}

.hero-actions {
    display: flex;
    gap: var(--spacing-lg);
    align-items: center;
}

.hero-visual {
    display: flex;
    justify-content: center;
    align-items: center;
    position: relative;
}

.hero-bg {
    width: 100%;
    height: auto;
    max-width: 600px;
    opacity: 0.8;
}

/* Services Section */
.services-section {
    background: linear-gradient(180deg, transparent 0%, rgba(0, 191, 255, 0.02) 50%, transparent 100%);
}

.services-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: var(--spacing-2xl);
}

.service-card {
    background: var(--bg-card);
    backdrop-filter: blur(10px);
    border: 1px solid var(--border-primary);
    border-radius: var(--border-radius-xl);
    padding: var(--spacing-2xl);
    transition: all var(--transition-normal);
    position: relative;
    overflow: hidden;
}

.service-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 3px;
    background: var(--gradient-primary);
    transform: scaleX(0);
    transition: transform var(--transition-normal);
}

.service-card:hover::before {
    transform: scaleX(1);
}

.service-card:hover {
    transform: translateY(-10px);
    border-color: var(--electric-blue);
    box-shadow: var(--shadow-xl), var(--shadow-glow);
}

.service-icon {
    width: 60px;
    height: 60px;
    background: var(--gradient-primary);
    border-radius: var(--border-radius-xl);
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: var(--spacing-lg);
    font-size: var(--font-size-2xl);
    color: var(--text-primary);
}

.service-title {
    font-size: var(--font-size-xl);
    font-weight: 600;
    margin-bottom: var(--spacing-md);
    color: var(--text-primary);
}

.service-description {
    color: var(--text-secondary);
    line-height: var(--leading-relaxed);
    margin-bottom: var(--spacing-lg);
}

.service-features {
    display: flex;
    flex-wrap: wrap;
    gap: var(--spacing-sm);
    margin-bottom: var(--spacing-lg);
}

.feature-tag {
    background: rgba(0, 191, 255, 0.1);
    color: var(--electric-blue);
    padding: var(--spacing-xs) var(--spacing-sm);
    border-radius: var(--border-radius-lg);
    font-size: var(--font-size-sm);
    font-weight: 500;
    border: 1px solid rgba(0, 191, 255, 0.2);
}

.service-cta {
    color: var(--electric-blue);
    font-weight: 600;
    text-decoration: none;
    display: inline-flex;
    align-items: center;
    gap: var(--spacing-xs);
    transition: all var(--transition-fast);
}

.service-cta:hover {
    color: var(--neon-green);
    transform: translateX(5px);
}

/* About Section */
.about-section {
    background: linear-gradient(180deg, rgba(57, 255, 20, 0.02) 0%, transparent 50%, rgba(0, 191, 255, 0.02) 100%);
}

.about-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-4xl);
    align-items: center;
}

.about-text h2 {
    font-size: var(--font-size-4xl);
    font-weight: 700;
    margin-bottom: var(--spacing-lg);
    background: var(--gradient-primary);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.about-description {
    color: var(--text-secondary);
    line-height: var(--leading-relaxed);
    margin-bottom: var(--spacing-xl);
    font-size: var(--font-size-lg);
}

.about-features {
    display: flex;
    flex-direction: column;
    gap: var(--spacing-lg);
}

.feature-item {
    display: flex;
    gap: var(--spacing-md);
    align-items: flex-start;
}

.feature-icon {
    width: 50px;
    height: 50px;
    background: var(--gradient-primary);
    border-radius: var(--border-radius-lg);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: var(--font-size-lg);
    color: var(--text-primary);
    flex-shrink: 0;
}

.feature-content h4 {
    font-size: var(--font-size-lg);
    font-weight: 600;
    margin-bottom: var(--spacing-xs);
    color: var(--text-primary);
}

.feature-content p {
    color: var(--text-secondary);
    line-height: var(--leading-relaxed);
    margin: 0;
}

.about-stats {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: var(--spacing-xl);
}

.stat-item {
    text-align: center;
    padding: var(--spacing-xl);
    background: var(--bg-card);
    border: 1px solid var(--border-primary);
    border-radius: var(--border-radius-xl);
    transition: all var(--transition-normal);
}

.stat-item:hover {
    transform: translateY(-5px);
    border-color: var(--electric-blue);
    box-shadow: var(--shadow-lg);
}

.stat-number {
    font-size: var(--font-size-4xl);
    font-weight: 700;
    background: var(--gradient-primary);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    margin-bottom: var(--spacing-sm);
}

.stat-label {
    color: var(--text-secondary);
    font-weight: 500;
    font-size: var(--font-size-sm);
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

/* Contact Section */
.contact-section {
    background: linear-gradient(180deg, rgba(255, 165, 0, 0.02) 0%, transparent 100%);
}

.contact-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-4xl);
}

.contact-info {
    display: flex;
    flex-direction: column;
    gap: var(--spacing-2xl);
}

.contact-item {
    display: flex;
    gap: var(--spacing-md);
    align-items: flex-start;
}

.contact-icon {
    width: 50px;
    height: 50px;
    background: var(--gradient-primary);
    border-radius: var(--border-radius-lg);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: var(--font-size-lg);
    color: var(--text-primary);
    flex-shrink: 0;
}

.contact-details h4 {
    font-size: var(--font-size-lg);
    font-weight: 600;
    margin-bottom: var(--spacing-xs);
    color: var(--text-primary);
}

.contact-details p {
    color: var(--text-secondary);
    margin: 0;
    line-height: var(--leading-relaxed);
}

.contact-form {
    background: var(--bg-card);
    backdrop-filter: blur(10px);
    border: 1px solid var(--border-primary);
    border-radius: var(--border-radius-xl);
    padding: var(--spacing-2xl);
}

/* About Page Specific Styles */
.about-content-section .about-grid {
    display: grid;
    grid-template-columns: 2fr 1fr;
    gap: var(--spacing-4xl);
    align-items: start;
}

.about-content-section .about-text {
    max-width: none;
}

.about-content-section .about-stats {
    grid-template-columns: 1fr;
    gap: var(--spacing-lg);
}

.team-section {
    background: linear-gradient(180deg, transparent 0%, rgba(0, 191, 255, 0.02) 50%, transparent 100%);
}

.team-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: var(--spacing-2xl);
}

.team-member {
    background: var(--bg-card);
    backdrop-filter: blur(10px);
    border: 1px solid var(--border-primary);
    border-radius: var(--border-radius-xl);
    padding: var(--spacing-2xl);
    text-align: center;
    transition: all var(--transition-normal);
}

.team-member:hover {
    transform: translateY(-10px);
    border-color: var(--electric-blue);
    box-shadow: var(--shadow-xl);
}

.member-avatar {
    width: 80px;
    height: 80px;
    background: var(--gradient-primary);
    border-radius: var(--border-radius-full);
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0 auto var(--spacing-lg) auto;
    font-size: var(--font-size-2xl);
    color: var(--text-primary);
}

.member-name {
    font-size: var(--font-size-xl);
    font-weight: 600;
    margin-bottom: var(--spacing-xs);
    color: var(--text-primary);
}

.member-role {
    color: var(--electric-blue);
    font-weight: 500;
    margin-bottom: var(--spacing-sm);
}

.member-bio {
    color: var(--text-secondary);
    line-height: var(--leading-relaxed);
    font-size: var(--font-size-sm);
    margin: 0;
}

/* Services Page Specific Styles */
.services-section .services-grid {
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
}

/* Contact Page Specific Styles */
.contact-section .social-links {
    display: flex;
    gap: var(--spacing-md);
    margin-top: var(--spacing-lg);
}

/* FAQ Section */
.faq-section {
    background: linear-gradient(180deg, transparent 0%, rgba(57, 255, 20, 0.02) 50%, transparent 100%);
}

.faq-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(400px, 1fr));
    gap: var(--spacing-xl);
}

.faq-item {
    background: var(--bg-card);
    backdrop-filter: blur(10px);
    border: 1px solid var(--border-primary);
    border-radius: var(--border-radius-xl);
    padding: var(--spacing-xl);
    transition: all var(--transition-normal);
}

.faq-item:hover {
    border-color: var(--electric-blue);
    box-shadow: var(--shadow-lg);
}

.faq-question {
    font-size: var(--font-size-lg);
    font-weight: 600;
    color: var(--text-primary);
    margin-bottom: var(--spacing-md);
}

.faq-answer {
    color: var(--text-secondary);
    line-height: var(--leading-relaxed);
    margin: 0;
}

/* Schedule Meeting Page Styles */
.scheduler-section .scheduler-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-4xl);
}

.meeting-benefits {
    display: flex;
    flex-direction: column;
    gap: var(--spacing-lg);
    margin-bottom: var(--spacing-2xl);
}

.benefit-item {
    display: flex;
    gap: var(--spacing-md);
    align-items: flex-start;
}

.benefit-icon {
    width: 50px;
    height: 50px;
    background: var(--gradient-primary);
    border-radius: var(--border-radius-lg);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: var(--font-size-lg);
    color: var(--text-primary);
    flex-shrink: 0;
}

.benefit-content h4 {
    font-size: var(--font-size-lg);
    font-weight: 600;
    margin-bottom: var(--spacing-xs);
    color: var(--text-primary);
}

.benefit-content p {
    color: var(--text-secondary);
    line-height: var(--leading-relaxed);
    margin: 0;
}

.meeting-types {
    margin-top: var(--spacing-2xl);
}

.meeting-types h3 {
    font-size: var(--font-size-xl);
    font-weight: 600;
    margin-bottom: var(--spacing-lg);
    color: var(--text-primary);
}

.meeting-type {
    display: flex;
    gap: var(--spacing-md);
    align-items: center;
    padding: var(--spacing-md);
    background: var(--bg-card);
    border: 1px solid var(--border-primary);
    border-radius: var(--border-radius-lg);
    margin-bottom: var(--spacing-md);
    transition: all var(--transition-fast);
}

.meeting-type:hover {
    border-color: var(--electric-blue);
    background: var(--bg-glass);
}

.type-icon {
    width: 40px;
    height: 40px;
    background: var(--gradient-primary);
    border-radius: var(--border-radius-lg);
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--text-primary);
    flex-shrink: 0;
}

.type-info h4 {
    font-size: var(--font-size-base);
    font-weight: 600;
    margin-bottom: var(--spacing-xs);
    color: var(--text-primary);
}

.type-info p {
    color: var(--text-secondary);
    font-size: var(--font-size-sm);
    margin: 0;
}

.scheduler-form {
    background: var(--bg-card);
    backdrop-filter: blur(10px);
    border: 1px solid var(--border-primary);
    border-radius: var(--border-radius-xl);
    padding: var(--spacing-2xl);
}

.meeting-form h3 {
    font-size: var(--font-size-2xl);
    font-weight: 600;
    margin-bottom: var(--spacing-xl);
    color: var(--text-primary);
    text-align: center;
}

.form-note {
    color: var(--text-muted);
    font-size: var(--font-size-sm);
    text-align: center;
    margin-top: var(--spacing-md);
    line-height: var(--leading-relaxed);
}

/* CTA Section */
.cta-section {
    background: linear-gradient(135deg, rgba(0, 191, 255, 0.1) 0%, rgba(57, 255, 20, 0.05) 100%);
    border-top: 1px solid var(--border-primary);
    border-bottom: 1px solid var(--border-primary);
}

.cta-content {
    text-align: center;
}

.cta-title {
    font-size: var(--font-size-4xl);
    font-weight: 700;
    margin-bottom: var(--spacing-md);
    background: var(--gradient-primary);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.cta-description {
    font-size: var(--font-size-xl);
    color: var(--text-secondary);
    margin-bottom: var(--spacing-2xl);
    line-height: var(--leading-relaxed);
}

.cta-actions {
    display: flex;
    gap: var(--spacing-lg);
    justify-content: center;
    align-items: center;
}

/* Responsive Design */
@media (max-width: 1023px) {
    .hero-content,
    .about-content,
    .contact-content,
    .scheduler-content,
    .about-grid {
        grid-template-columns: 1fr;
        gap: var(--spacing-2xl);
    }

    .hero-visual {
        order: -1;
    }

    .about-stats {
        grid-template-columns: repeat(2, 1fr);
    }

    .services-grid {
        grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    }

    .faq-grid {
        grid-template-columns: 1fr;
    }
}

@media (max-width: 767px) {
    .hero-text h1 {
        font-size: var(--font-size-4xl);
    }

    .hero-text .subtitle {
        font-size: var(--font-size-lg);
    }

    .hero-actions {
        flex-direction: column;
        align-items: stretch;
    }

    .about-stats {
        grid-template-columns: 1fr;
    }

    .services-grid {
        grid-template-columns: 1fr;
    }

    .team-grid {
        grid-template-columns: 1fr;
    }

    .cta-actions {
        flex-direction: column;
        align-items: stretch;
    }

    .service-card,
    .stat-item,
    .team-member,
    .faq-item,
    .contact-form,
    .scheduler-form {
        padding: var(--spacing-lg);
    }
}

src/styles/pages/services.css.content:
/* Service Pages Styles */

/* Page Header */
.page-header {
    background: linear-gradient(135deg, #1a1a1a 0%, #2d2d2d 100%);
    padding: 120px 0 80px;
    text-align: center;
}

.breadcrumb {
    margin-bottom: 20px;
    opacity: 0.8;
}

.breadcrumb a {
    color: #00BFFF;
    text-decoration: none;
    transition: color 0.3s ease;
}

.breadcrumb a:hover {
    color: #39FF14;
}

.breadcrumb span {
    color: #666;
    margin: 0 10px;
}

.page-title {
    font-size: 3.5rem;
    font-weight: 700;
    color: #fff;
    margin-bottom: 20px;
    background: linear-gradient(135deg, #00BFFF, #39FF14);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.page-subtitle {
    font-size: 1.3rem;
    color: #ccc;
    max-width: 600px;
    margin: 0 auto;
}

/* Service Overview */
.service-overview {
    padding: 100px 0;
}

.service-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 80px;
    align-items: center;
}

.service-description h2 {
    font-size: 2.5rem;
    color: #fff;
    margin-bottom: 30px;
    font-weight: 600;
}

.service-description p {
    font-size: 1.2rem;
    color: #ccc;
    line-height: 1.8;
    margin-bottom: 40px;
}

.service-features {
    display: flex;
    flex-direction: column;
    gap: 30px;
}

.feature-item {
    display: flex;
    align-items: flex-start;
    gap: 20px;
    padding: 25px;
    background: rgba(255, 255, 255, 0.02);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 12px;
    transition: all 0.3s ease;
}

.feature-item:hover {
    background: rgba(0, 191, 255, 0.05);
    border-color: rgba(0, 191, 255, 0.3);
    transform: translateY(-2px);
}

.feature-item i {
    font-size: 2rem;
    color: #00BFFF;
    flex-shrink: 0;
    margin-top: 5px;
}

.feature-item h4 {
    font-size: 1.3rem;
    color: #fff;
    margin-bottom: 8px;
    font-weight: 600;
}

.feature-item p {
    color: #ccc;
    margin: 0;
    font-size: 1rem;
    line-height: 1.6;
}

.service-image {
    display: flex;
    justify-content: center;
    align-items: center;
}

.placeholder-image {
    width: 400px;
    height: 300px;
    background: linear-gradient(135deg, rgba(0, 191, 255, 0.1), rgba(57, 255, 20, 0.1));
    border: 2px dashed rgba(0, 191, 255, 0.3);
    border-radius: 12px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 20px;
}

.placeholder-image i {
    font-size: 4rem;
    color: #00BFFF;
}

.placeholder-image p {
    color: #ccc;
    font-size: 1.1rem;
    margin: 0;
}

/* Process Steps */
.seo-process, .design-process {
    padding: 100px 0;
    background: rgba(255, 255, 255, 0.02);
}

.section-title {
    font-size: 2.5rem;
    color: #fff;
    text-align: center;
    margin-bottom: 60px;
    font-weight: 600;
}

.process-steps {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 40px;
    max-width: 1000px;
    margin: 0 auto;
}

.process-step {
    text-align: center;
    padding: 40px 30px;
    background: rgba(255, 255, 255, 0.02);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 12px;
    transition: all 0.3s ease;
}

.process-step:hover {
    background: rgba(0, 191, 255, 0.05);
    border-color: rgba(0, 191, 255, 0.3);
    transform: translateY(-5px);
}

.step-number {
    width: 60px;
    height: 60px;
    background: linear-gradient(135deg, #00BFFF, #39FF14);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    font-weight: 700;
    color: #000;
    margin: 0 auto 20px;
}

.process-step h3 {
    font-size: 1.4rem;
    color: #fff;
    margin-bottom: 15px;
    font-weight: 600;
}

.process-step p {
    color: #ccc;
    line-height: 1.6;
    margin: 0;
}

/* Grid Sections */
.social-platforms, .campaign-types, .testing-types, .consulting-areas, 
.support-plans, .components-grid, .strategy-components, .software-types {
    padding: 100px 0;
}

.platforms-grid, .types-grid, .plans-grid, .components-grid, .areas-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 30px;
}

.platform-card, .type-card, .plan-card, .component-card, .area-card {
    background: rgba(255, 255, 255, 0.02);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 12px;
    padding: 40px 30px;
    text-align: center;
    transition: all 0.3s ease;
}

.platform-card:hover, .type-card:hover, .plan-card:hover, 
.component-card:hover, .area-card:hover {
    background: rgba(0, 191, 255, 0.05);
    border-color: rgba(0, 191, 255, 0.3);
    transform: translateY(-5px);
}

.platform-card i, .type-card i, .plan-card i, 
.component-card i, .area-card i {
    font-size: 3rem;
    color: #00BFFF;
    margin-bottom: 20px;
}

.platform-card h3, .type-card h3, .plan-card h3, 
.component-card h3, .area-card h3 {
    font-size: 1.4rem;
    color: #fff;
    margin-bottom: 15px;
    font-weight: 600;
}

.platform-card p, .type-card p, .plan-card p, 
.component-card p, .area-card p {
    color: #ccc;
    line-height: 1.6;
    margin: 0;
}

/* Responsive Design */
@media (max-width: 768px) {
    .page-title {
        font-size: 2.5rem;
    }

    .service-content {
        grid-template-columns: 1fr;
        gap: 50px;
    }

    .service-description h2 {
        font-size: 2rem;
    }

    .placeholder-image {
        width: 100%;
        max-width: 350px;
        height: 250px;
    }

    .process-steps {
        grid-template-columns: 1fr;
    }

    .platforms-grid, .types-grid, .plans-grid, .components-grid, .areas-grid {
        grid-template-columns: 1fr;
    }
}

@media (max-width: 480px) {
    .page-header {
        padding: 100px 0 60px;
    }

    .page-title {
        font-size: 2rem;
    }

    .service-overview {
        padding: 60px 0;
    }

    .seo-process, .design-process, .social-platforms, .campaign-types,
    .testing-types, .consulting-areas, .support-plans, .strategy-components, 
    .software-types {
        padding: 60px 0;
    }
}

src/components/forms/contact/contact-for.content:
/* Contact Form Component Styles */

.contact-form-section {
    padding: var(--spacing-5xl) 0;
    background: var(--bg-primary);
    position: relative;
}

/* Form Header */
.form-header {
    text-align: center;
    margin-bottom: var(--spacing-3xl);
}

.contact-form-container {
    display: grid;
    grid-template-columns: 1fr 1.2fr;
    gap: var(--spacing-4xl);
    align-items: start;
}

.form-title {
    font-size: clamp(2rem, 4vw, 3rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--text-primary);
    margin-bottom: var(--spacing-md);
}

.form-subtitle {
    font-size: var(--font-size-lg);
    color: var(--text-secondary);
    max-width: 600px;
    margin: 0 auto;
    line-height: 1.6;
}

/* Contact Info */
.contact-info {
    order: -1;
}

.contact-info-card {
    background: rgba(0, 0, 0, 0.4);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: var(--border-radius-xl);
    padding: var(--spacing-2xl);
    position: relative;
    overflow: hidden;
    height: fit-content;
    position: sticky;
    top: var(--spacing-2xl);
}

.contact-info-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(0, 191, 255, 0.03), rgba(57, 255, 20, 0.03));
    pointer-events: none;
}

.contact-info-card h3 {
    font-size: var(--font-size-xl);
    font-weight: 600;
    color: var(--text-primary);
    margin-bottom: var(--spacing-md);
    position: relative;
    z-index: 2;
}

.contact-intro {
    color: var(--text-secondary);
    font-size: var(--font-size-md);
    line-height: 1.6;
    margin-bottom: var(--spacing-xl);
    position: relative;
    z-index: 2;
}

.contact-methods {
    position: relative;
    z-index: 2;
}

.contact-method {
    display: flex;
    align-items: flex-start;
    gap: var(--spacing-md);
    margin-bottom: var(--spacing-lg);
    padding: var(--spacing-md);
    border-radius: var(--border-radius-md);
    transition: all var(--transition-normal);
    position: relative;
}

.contact-method:hover {
    background: rgba(255, 255, 255, 0.05);
    transform: translateX(4px);
}

.contact-icon {
    width: 48px;
    height: 48px;
    background: linear-gradient(135deg, var(--electric-blue), var(--neon-green));
    border-radius: var(--border-radius-md);
    display: flex;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
    position: relative;
    overflow: hidden;
}

.contact-icon::before {
    content: '';
    position: absolute;
    inset: 1px;
    background: var(--bg-primary);
    border-radius: calc(var(--border-radius-md) - 1px);
}

.contact-icon svg {
    color: var(--electric-blue);
    position: relative;
    z-index: 1;
    transition: color var(--transition-normal);
}

.contact-method:hover .contact-icon svg {
    color: var(--neon-green);
}

.contact-details h4 {
    color: var(--text-primary);
    font-size: var(--font-size-md);
    font-weight: 600;
    margin-bottom: var(--spacing-xs);
}

.contact-details p,
.contact-details a {
    color: var(--text-secondary);
    font-size: var(--font-size-md);
    line-height: 1.5;
    text-decoration: none;
    transition: color var(--transition-normal);
}

.contact-details a:hover {
    color: var(--electric-blue);
}

.contact-label {
    display: block;
    color: var(--text-tertiary);
    font-size: var(--font-size-sm);
    margin-top: var(--spacing-xs);
    opacity: 0.8;
}

/* Social Links */
.social-links {
    margin-top: var(--spacing-2xl);
    padding-top: var(--spacing-xl);
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    position: relative;
    z-index: 2;
}

.social-links h4 {
    color: var(--text-primary);
    font-size: var(--font-size-md);
    font-weight: 600;
    margin-bottom: var(--spacing-md);
}

.social-icons {
    display: flex;
    gap: var(--spacing-md);
}

.social-icons a {
    width: 40px;
    height: 40px;
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: var(--border-radius-md);
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--text-secondary);
    text-decoration: none;
    transition: all var(--transition-normal);
    position: relative;
    overflow: hidden;
}

.social-icons a::before {
    content: '';
    position: absolute;
    inset: 0;
    background: linear-gradient(135deg, var(--electric-blue), var(--neon-green));
    opacity: 0;
    transition: opacity var(--transition-normal);
}

.social-icons a:hover {
    color: var(--text-primary);
    transform: translateY(-2px);
    box-shadow: 0 4px 20px rgba(0, 191, 255, 0.3);
}

.social-icons a:hover::before {
    opacity: 0.1;
}

.social-icons a svg {
    position: relative;
    z-index: 1;
}

/* Contact Form */
.contact-form {
    background: rgba(0, 0, 0, 0.4);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: var(--border-radius-xl);
    padding: var(--spacing-2xl);
    position: relative;
    overflow: hidden;
}

.contact-form::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(0, 191, 255, 0.03), rgba(57, 255, 20, 0.03));
    pointer-events: none;
}

/* Form Grid */
.form-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-lg);
    margin-bottom: var(--spacing-lg);
}

/* Form Group */
.form-group {
    position: relative;
    margin-bottom: var(--spacing-lg);
}

.form-group.full-width {
    grid-column: 1 / -1;
}

/* Form Inputs */
.form-input,
.form-select,
.form-textarea {
    width: 100%;
    padding: var(--spacing-md) var(--spacing-lg);
    background: rgba(0, 0, 0, 0.3);
    border: 2px solid rgba(255, 255, 255, 0.1);
    border-radius: var(--border-radius-md);
    color: var(--text-primary);
    font-size: var(--font-size-base);
    font-family: inherit;
    transition: all var(--transition-normal);
    position: relative;
    z-index: 2;
}

.form-input:focus,
.form-select:focus,
.form-textarea:focus {
    outline: none;
    border-color: var(--electric-blue);
    background: rgba(0, 191, 255, 0.05);
    box-shadow: 0 0 20px rgba(0, 191, 255, 0.2);
}

.form-input:valid,
.form-select:valid,
.form-textarea:valid {
    border-color: var(--neon-green);
}

.form-input.error,
.form-select.error,
.form-textarea.error {
    border-color: #ff4757;
    background: rgba(255, 71, 87, 0.05);
}

/* Form Labels */
.form-label {
    position: absolute;
    left: var(--spacing-lg);
    top: 50%;
    transform: translateY(-50%);
    color: var(--text-secondary);
    font-size: var(--font-size-base);
    pointer-events: none;
    transition: all var(--transition-normal);
    background: var(--bg-primary);
    padding: 0 var(--spacing-xs);
    z-index: 3;
}

.form-textarea + .form-label {
    top: var(--spacing-lg);
    transform: none;
}

.form-input:focus + .form-label,
.form-input:valid + .form-label,
.form-select:focus + .form-label,
.form-select:valid + .form-label,
.form-textarea:focus + .form-label,
.form-textarea:valid + .form-label {
    top: 0;
    transform: translateY(-50%);
    font-size: var(--font-size-sm);
    color: var(--electric-blue);
}

/* Form Select */
.form-select {
    cursor: pointer;
    background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 20 20'%3e%3cpath stroke='%236b7280' stroke-linecap='round' stroke-linejoin='round' stroke-width='1.5' d='m6 8 4 4 4-4'/%3e%3c/svg%3e");
    background-position: right var(--spacing-md) center;
    background-repeat: no-repeat;
    background-size: 1.5em 1.5em;
    padding-right: var(--spacing-2xl);
    appearance: none;
}

.form-select:focus {
    background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 20 20'%3e%3cpath stroke='%2300bfff' stroke-linecap='round' stroke-linejoin='round' stroke-width='1.5' d='m6 8 4 4 4-4'/%3e%3c/svg%3e");
}

/* Textarea */
.form-textarea {
    resize: vertical;
    min-height: 120px;
    padding-top: var(--spacing-lg);
}

/* Form Errors */
.form-error {
    color: #ff4757;
    font-size: var(--font-size-sm);
    margin-top: var(--spacing-xs);
    opacity: 0;
    transform: translateY(-10px);
    transition: all var(--transition-normal);
    position: relative;
    z-index: 2;
}

.form-error.show {
    opacity: 1;
    transform: translateY(0);
}

/* Form Hint */
.form-hint {
    color: var(--text-secondary);
    font-size: var(--font-size-sm);
    margin-top: var(--spacing-xs);
    opacity: 0.8;
}

/* Checkbox Wrapper */
.checkbox-wrapper {
    display: flex;
    align-items: flex-start;
    gap: var(--spacing-sm);
    cursor: pointer;
    line-height: 1.5;
}

.form-checkbox {
    opacity: 0;
    position: absolute;
}

.checkbox-custom {
    width: 20px;
    height: 20px;
    border: 2px solid rgba(255, 255, 255, 0.3);
    border-radius: 4px;
    background: transparent;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all var(--transition-normal);
    flex-shrink: 0;
    margin-top: 2px;
}

.checkbox-custom::after {
    content: '';
    width: 10px;
    height: 6px;
    border: 2px solid var(--electric-blue);
    border-top: none;
    border-right: none;
    transform: rotate(-45deg) scale(0);
    transition: transform var(--transition-normal);
}

.form-checkbox:checked + .checkbox-custom {
    background: rgba(0, 191, 255, 0.1);
    border-color: var(--electric-blue);
}

.form-checkbox:checked + .checkbox-custom::after {
    transform: rotate(-45deg) scale(1);
}

.checkbox-text {
    color: var(--text-secondary);
    font-size: var(--font-size-sm);
}

.form-link {
    color: var(--electric-blue);
    text-decoration: none;
    transition: color var(--transition-normal);
}

.form-link:hover {
    color: var(--neon-green);
    text-decoration: underline;
}

/* Form Actions */
.form-actions {
    display: flex;
    gap: var(--spacing-md);
    margin-top: var(--spacing-xl);
}

.form-submit {
    flex: 1;
    position: relative;
    overflow: hidden;
}

.btn-text,
.btn-icon {
    transition: all var(--transition-normal);
}

.btn-loader {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    opacity: 0;
    transition: opacity var(--transition-normal);
}

.btn-loader svg {
    animation: spin 1s linear infinite;
}

.form-submit.loading .btn-text,
.form-submit.loading .btn-icon {
    opacity: 0;
}

.form-submit.loading .btn-loader {
    opacity: 1;
}

@keyframes spin {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
}

/* Form Success/Error Messages */
.form-success,
.form-error-message {
    display: none;
    text-align: center;
    padding: var(--spacing-xl);
    border-radius: var(--border-radius-md);
    margin-top: var(--spacing-lg);
}

.form-success {
    background: rgba(57, 255, 20, 0.1);
    border: 1px solid rgba(57, 255, 20, 0.3);
    color: var(--neon-green);
}

.form-error-message {
    background: rgba(255, 71, 87, 0.1);
    border: 1px solid rgba(255, 71, 87, 0.3);
    color: #ff4757;
}

.form-success.show,
.form-error-message.show {
    display: block;
    animation: slideIn 0.5s ease-out;
}

.form-success h3,
.form-error-message h3 {
    margin: var(--spacing-sm) 0;
    font-size: var(--font-size-lg);
}

.form-success p,
.form-error-message p {
    margin: 0;
    opacity: 0.9;
}

@keyframes slideIn {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}



/* Responsive Design */
@media (max-width: 1023px) {
    .contact-form-container {
        grid-template-columns: 1fr;
        gap: var(--spacing-2xl);
    }

    .contact-info {
        order: 0;
    }

    .contact-info-card {
        position: static;
    }

    .form-grid {
        grid-template-columns: 1fr;
        gap: var(--spacing-md);
    }
}

@media (max-width: 767px) {
    .contact-form-section {
        padding: var(--spacing-4xl) 0;
    }

    .form-header {
        margin-bottom: var(--spacing-2xl);
    }

    .form-title {
        font-size: 2rem;
    }

    .contact-form-container {
        gap: var(--spacing-xl);
    }

    .contact-form,
    .contact-info-card {
        padding: var(--spacing-xl);
    }

    .contact-method {
        padding: var(--spacing-sm);
    }

    .contact-icon {
        width: 40px;
        height: 40px;
    }

    .contact-icon svg {
        width: 20px;
        height: 20px;
    }

    .form-actions {
        flex-direction: column;
        gap: var(--spacing-sm);
    }

    .social-icons {
        justify-content: center;
    }
}

@media (max-width: 480px) {
    .form-header {
        margin-bottom: var(--spacing-lg);
    }

    .contact-form {
        padding: var(--spacing-md);
    }

    .contact-info-card {
        padding: var(--spacing-md);
    }

    .contact-method {
        flex-direction: column;
        text-align: center;
        gap: var(--spacing-sm);
    }
}

src/components/navigation/mega-menu/mega.content:
/* Mega Menu Component Styles */

.mega-menu {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    z-index: 9999;
    opacity: 0;
    visibility: hidden;
    pointer-events: none;
    transition: all var(--transition-normal);
}

.mega-menu.active {
    opacity: 1;
    visibility: visible;
    pointer-events: all;
}

.mega-menu-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.8);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
}

.mega-menu-content {
    position: absolute;
    top: 80px;
    left: 50%;
    transform: translateX(-50%);
    background: rgba(0, 0, 0, 0.95);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: var(--border-radius-xl);
    padding: var(--spacing-lg);
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: var(--spacing-lg);
    width: auto;
    max-width: 750px;
    min-width: 520px;
    box-shadow: 0 25px 50px rgba(0, 0, 0, 0.7);
    transform: translateX(-50%) translateY(-20px);
    transition: all var(--transition-normal);
}

.mega-menu.active .mega-menu-content {
    transform: translateX(-50%) translateY(0);
}

.mega-menu-column {
    display: flex;
    flex-direction: column;
}

.mega-menu-title {
    display: flex;
    align-items: center;
    gap: var(--spacing-sm);
    color: var(--text-primary);
    font-size: var(--font-size-lg);
    font-weight: 600;
    margin-bottom: var(--spacing-lg);
    padding-bottom: var(--spacing-sm);
    border-bottom: 2px solid var(--electric-blue);
}

.mega-menu-title svg {
    color: var(--electric-blue);
}

.mega-menu-list {
    list-style: none;
    padding: 0;
    margin: 0;
}

.mega-menu-list li {
    margin-bottom: 2px;
}

.mega-menu-link {
    display: flex;
    align-items: center;
    gap: var(--spacing-sm);
    padding: var(--spacing-sm) var(--spacing-md);
    color: var(--text-secondary);
    text-decoration: none;
    border-radius: var(--border-radius-md);
    transition: all var(--transition-fast);
    position: relative;
    overflow: hidden;
    white-space: nowrap;
}

.mega-menu-link::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(0, 191, 255, 0.1), transparent);
    transition: left 0.5s;
}

.mega-menu-link:hover::before {
    left: 100%;
}

.mega-menu-link:hover {
    background: rgba(0, 191, 255, 0.1);
    color: var(--electric-blue);
    transform: translateX(5px);
    box-shadow: 0 4px 15px rgba(0, 191, 255, 0.2);
}

.mega-menu-link svg {
    color: var(--electric-blue);
    flex-shrink: 0;
    transition: transform var(--transition-fast);
}

.mega-menu-link:hover svg {
    transform: scale(1.1);
}

.mega-menu-link span {
    font-weight: 500;
    font-size: var(--font-size-base);
    color: var(--text-primary);
    transition: color var(--transition-fast);
}

.mega-menu-link:hover span {
    color: var(--electric-blue);
}

/* Desktop Only Design - Hide completely on mobile/tablet */
@media (max-width: 1023px) {
    .mega-menu {
        display: none !important;
        opacity: 0 !important;
        visibility: hidden !important;
        pointer-events: none !important;
    }

    .mega-menu.active {
        display: none !important;
        opacity: 0 !important;
        visibility: hidden !important;
        pointer-events: none !important;
    }

    .nav-dropdown .mega-menu {
        display: none !important;
    }
}

/* Animation for menu items */
.mega-menu-column {
    animation: fadeInUp 0.6s ease-out forwards;
}

.mega-menu-column:nth-child(1) {
    animation-delay: 0.1s;
}

.mega-menu-column:nth-child(2) {
    animation-delay: 0.2s;
}

.mega-menu-column:nth-child(3) {
    animation-delay: 0.3s;
}

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

src/components/navigation/mobile-nav/mob.content:
/* Mobile Navigation Component Styles */

/* Fallback CSS Variables for Mobile Navigation */
:root {
    --spacing-xs: 0.25rem;
    --spacing-sm: 0.5rem;
    --spacing-md: 1rem;
    --spacing-lg: 1.5rem;
    --spacing-xl: 2rem;
    --font-size-xs: 0.75rem;
    --font-size-sm: 0.875rem;
    --font-size-base: 1rem;
    --font-size-lg: 1.125rem;
    --border-radius-md: 0.5rem;
    --border-radius-lg: 0.75rem;
    --transition-fast: 0.2s ease;
    --transition-normal: 0.3s ease;
    --electric-blue: #00BFFF;
    --neon-green: #39FF14;
    --text-secondary: #a0a0a0;
    --black: #000000;
}

.mobile-nav {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    z-index: 99999;
    opacity: 0;
    visibility: hidden;
    pointer-events: none;
    transition: all var(--transition-normal);
}

.mobile-nav.active {
    opacity: 1;
    visibility: visible;
    pointer-events: all;
}

.mobile-nav-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.9);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
}

.mobile-nav-content {
    position: absolute;
    top: 0;
    right: 0;
    width: 85%;
    max-width: 400px;
    height: 100%;
    background: rgba(0, 0, 0, 0.98);
    backdrop-filter: blur(30px);
    -webkit-backdrop-filter: blur(30px);
    border-left: 1px solid rgba(255, 255, 255, 0.1);
    display: flex;
    flex-direction: column;
    transform: translateX(100%);
    transition: transform var(--transition-normal);
    overflow-y: auto;
}

.mobile-nav.active .mobile-nav-content {
    transform: translateX(0);
}

.mobile-nav-header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: var(--spacing-lg);
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    background: rgba(0, 0, 0, 0.5);
}

.mobile-nav-logo {
    display: flex;
    align-items: center;
    gap: var(--spacing-sm);
}

.nav-logo {
    width: 32px;
    height: 32px;
}

.nav-brand-text {
    font-weight: 600;
    font-size: var(--font-size-lg);
    background: linear-gradient(135deg, var(--electric-blue), var(--neon-green));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.mobile-nav-close {
    background: none;
    border: none;
    color: var(--text-secondary);
    cursor: pointer;
    padding: var(--spacing-xs);
    border-radius: var(--border-radius-md);
    transition: all var(--transition-fast);
    display: flex;
    align-items: center;
    justify-content: center;
}

.mobile-nav-close:hover {
    color: var(--electric-blue);
    background: rgba(0, 191, 255, 0.1);
    transform: rotate(90deg);
}

.mobile-nav-menu {
    flex: 1;
    padding: var(--spacing-lg);
}

.mobile-nav-list {
    list-style: none;
    padding: 0;
    margin: 0;
}

.mobile-nav-item {
    margin-bottom: var(--spacing-sm);
}

.mobile-nav-link {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: var(--spacing-md);
    color: var(--text-secondary);
    text-decoration: none;
    font-size: var(--font-size-base);
    font-weight: 500;
    border-radius: var(--border-radius-md);
    transition: all var(--transition-fast);
    background: none;
    border: none;
    width: 100%;
    cursor: pointer;
    position: relative;
    overflow: hidden;
    text-align: left;
}

.mobile-nav-link::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(0, 191, 255, 0.1), transparent);
    transition: left 0.5s;
}

.mobile-nav-link:hover::before {
    left: 100%;
}

.mobile-nav-link:hover,
.mobile-nav-link.active {
    color: var(--electric-blue);
    background: rgba(0, 191, 255, 0.1);
    transform: translateX(8px);
    box-shadow: 0 4px 15px rgba(0, 191, 255, 0.2);
}

.nav-link-content {
    display: flex;
    align-items: center;
    gap: var(--spacing-md);
    flex: 1;
    text-align: left;
}

.mobile-nav-link svg {
    flex-shrink: 0;
    transition: transform var(--transition-fast);
}

.mobile-nav-link:hover svg {
    transform: scale(1.1);
}

.dropdown-arrow {
    transition: transform var(--transition-fast);
}

.mobile-nav-link.active .dropdown-arrow {
    transform: rotate(180deg);
}

/* Mobile Dropdown */
.mobile-dropdown {
    max-height: 0;
    overflow: hidden;
    transition: max-height 0.4s ease, padding 0.4s ease, opacity 0.4s ease;
    background: rgba(0, 191, 255, 0.05);
    border-radius: var(--border-radius-md);
    margin-top: var(--spacing-xs);
    opacity: 0;
    border-left: 2px solid transparent;
}

.mobile-dropdown.active {
    max-height: 800px;
    padding: var(--spacing-md) 0;
    opacity: 1;
    border-left-color: var(--electric-blue);
}

/* Mobile Dropdown Sections */
.mobile-dropdown-section {
    margin-bottom: var(--spacing-md);
    padding: 0 var(--spacing-md);
}

.mobile-dropdown-section:last-child {
    margin-bottom: 0;
}

.mobile-section-title {
    font-size: var(--font-size-sm);
    font-weight: 600;
    color: var(--electric-blue);
    margin-bottom: var(--spacing-sm);
    padding: var(--spacing-xs) 0;
    border-bottom: 1px solid rgba(0, 191, 255, 0.2);
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.mobile-dropdown-link {
    display: flex;
    align-items: center;
    gap: var(--spacing-sm);
    padding: var(--spacing-sm) var(--spacing-md);
    color: var(--text-secondary);
    text-decoration: none;
    font-size: var(--font-size-sm);
    transition: all var(--transition-fast);
    border-left: 2px solid transparent;
    border-radius: var(--border-radius-sm);
    margin: var(--spacing-xs) 0;
}

.mobile-dropdown-link:hover {
    color: var(--electric-blue);
    background: rgba(0, 191, 255, 0.1);
    border-left-color: var(--electric-blue);
    transform: translateX(4px);
}

.mobile-dropdown-link i {
    width: 16px;
    text-align: center;
    opacity: 0.8;
}

/* Mobile CTA */
.mobile-nav-cta {
    margin-top: var(--spacing-xl);
    padding-top: var(--spacing-lg);
    border-top: 1px solid rgba(255, 255, 255, 0.1);
}

.mobile-cta {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: var(--spacing-sm);
    width: 100%;
    padding: var(--spacing-md);
    background: linear-gradient(135deg, var(--electric-blue), var(--neon-green));
    color: var(--black);
    text-decoration: none;
    font-weight: 600;
    border-radius: var(--border-radius-lg);
    transition: all var(--transition-fast);
    box-shadow: 0 4px 15px rgba(0, 191, 255, 0.3);
}

.mobile-cta:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(0, 191, 255, 0.4);
    filter: brightness(1.1);
}

/* Mobile dropdown show state */
.mobile-dropdown.show {
    max-height: 800px;
    opacity: 1;
}

.dropdown-arrow {
    transition: transform var(--transition-fast);
}

.dropdown-arrow.rotated {
    transform: rotate(180deg);
}

/* Mobile Navigation Footer */
.mobile-nav-footer {
    padding: var(--spacing-lg);
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    background: rgba(0, 0, 0, 0.3);
}

.mobile-social-links {
    display: flex;
    gap: var(--spacing-md);
    justify-content: center;
    margin-bottom: var(--spacing-md);
}

.mobile-social-link {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 40px;
    height: 40px;
    background: rgba(255, 255, 255, 0.1);
    color: var(--text-secondary);
    text-decoration: none;
    border-radius: var(--border-radius-md);
    transition: all var(--transition-fast);
}

.mobile-social-link:hover {
    background: rgba(0, 191, 255, 0.2);
    color: var(--electric-blue);
    transform: translateY(-2px);
}

.mobile-nav-copyright {
    text-align: center;
    color: var(--text-secondary);
    font-size: var(--font-size-xs);
    margin: 0;
    opacity: 0.8;
}

/* Responsive adjustments for mobile nav */
@media (max-width: 480px) {
    .mobile-nav-content {
        width: 95%;
        max-width: none;
    }

    .mobile-nav-header {
        padding: var(--spacing-md);
    }

    .mobile-nav-menu {
        padding: var(--spacing-md);
    }

    .mobile-nav-link {
        padding: var(--spacing-sm) var(--spacing-md);
    }
}
